import express from 'express';
import bcrypt from 'bcryptjs';
import jwt from 'jsonwebtoken';
import { storage } from './storage';
import { z } from 'zod';

const router = express.Router();

// Validation schemas
const registerSchema = z.object({
  username: z.string().min(3).max(20),
  password: z.string().min(6).max(100),
  email: z.string().email().optional(),
});

const loginSchema = z.object({
  username: z.string(),
  password: z.string(),
});

// Register new user
router.post('/register', async (req, res) => {
  try {
    const { username, password, email } = registerSchema.parse(req.body);

    // Check if user already exists
    const existingUser = await storage.getUserByUsername(username);
    if (existingUser) {
      return res.status(400).json({ message: 'Username already exists' });
    }

    if (email) {
      const existingEmail = await storage.getUserByEmail(email);
      if (existingEmail) {
        return res.status(400).json({ message: 'Email already exists' });
      }
    }

    // Hash password
    const saltRounds = 12;
    const hashedPassword = await bcrypt.hash(password, saltRounds);

    // Create user
    const user = await storage.createUser({
      username,
      password: hashedPassword,
      email: email || null,
    });

    // Generate JWT token
    const jwtSecret = process.env.SESSION_SECRET;
    if (!jwtSecret) {
      throw new Error('SESSION_SECRET environment variable is required');
    }

    const token = jwt.sign(
      { userId: user.id, username: user.username },
      jwtSecret,
      { expiresIn: '7d' }
    );

    res.status(201).json({
      message: 'User created successfully',
      user: {
        id: user.id,
        username: user.username,
        email: user.email,
        balance: user.balance,
        totalWinnings: user.totalWinnings,
        totalLosses: user.totalLosses,
        handsPlayed: user.handsPlayed,
        handsWon: user.handsWon,
        gamesPlayed: user.gamesPlayed,
        gamesWon: user.gamesWon,
        createdAt: user.createdAt,
      },
      token,
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: 'Invalid input', errors: error.errors });
    }
    console.error('Registration error:', error);
    res.status(500).json({ message: 'Internal server error' });
  }
});

// Login user
router.post('/login', async (req, res) => {
  try {
    const { username, password } = loginSchema.parse(req.body);

    // Find user
    const user = await storage.getUserByUsername(username);
    if (!user) {
      return res.status(401).json({ message: 'Invalid credentials' });
    }

    // Verify password
    const isPasswordValid = await bcrypt.compare(password, user.password);
    if (!isPasswordValid) {
      return res.status(401).json({ message: 'Invalid credentials' });
    }

    // Generate JWT token
    const jwtSecret = process.env.SESSION_SECRET;
    if (!jwtSecret) {
      throw new Error('SESSION_SECRET environment variable is required');
    }

    const token = jwt.sign(
      { userId: user.id, username: user.username },
      jwtSecret,
      { expiresIn: '7d' }
    );

    res.json({
      message: 'Login successful',
      user: {
        id: user.id,
        username: user.username,
        email: user.email,
        balance: user.balance,
        totalWinnings: user.totalWinnings,
        totalLosses: user.totalLosses,
        handsPlayed: user.handsPlayed,
        handsWon: user.handsWon,
        gamesPlayed: user.gamesPlayed,
        gamesWon: user.gamesWon,
        createdAt: user.createdAt,
      },
      token,
    });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ message: 'Invalid input', errors: error.errors });
    }
    console.error('Login error:', error);
    res.status(500).json({ message: 'Internal server error' });
  }
});

// Get current user profile
router.get('/profile', authenticateToken, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const user = await storage.getUser(userId);

    if (!user) {
      return res.status(404).json({ message: 'User not found' });
    }

    res.json({
      user: {
        id: user.id,
        username: user.username,
        email: user.email,
        balance: user.balance,
        totalWinnings: user.totalWinnings,
        totalLosses: user.totalLosses,
        handsPlayed: user.handsPlayed,
        handsWon: user.handsWon,
        gamesPlayed: user.gamesPlayed,
        gamesWon: user.gamesWon,
        createdAt: user.createdAt,
        updatedAt: user.updatedAt,
      },
    });
  } catch (error) {
    console.error('Profile error:', error);
    res.status(500).json({ message: 'Internal server error' });
  }
});

// Update user balance (for admin or game results)
router.post('/update-balance', authenticateToken, async (req, res) => {
  try {
    const userId = (req as any).user.userId;
    const { balance } = req.body;

    if (typeof balance !== 'string' || isNaN(parseFloat(balance))) {
      return res.status(400).json({ message: 'Invalid balance amount' });
    }

    await storage.updateUserBalance(userId, balance);

    res.json({ message: 'Balance updated successfully' });
  } catch (error) {
    console.error('Balance update error:', error);
    res.status(500).json({ message: 'Internal server error' });
  }
});

// Verify JWT token for socket authentication
export function verifyToken(token: string): { userId: string; username: string } | null {
  try {
    const jwtSecret = process.env.SESSION_SECRET;
    if (!jwtSecret) {
      throw new Error('SESSION_SECRET environment variable is required');
    }

    const decoded = jwt.verify(token, jwtSecret) as any;
    return {
      userId: decoded.userId,
      username: decoded.username
    };
  } catch (error) {
    console.error('Token verification failed:', error);
    return null;
  }
}

// Middleware to authenticate JWT token
export function authenticateToken(req: express.Request, res: express.Response, next: express.NextFunction) {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

  if (!token) {
    return res.status(401).json({ message: 'Access token required' });
  }

  const jwtSecret = process.env.SESSION_SECRET;
  if (!jwtSecret) {
    return res.status(500).json({ message: 'Server configuration error' });
  }

  jwt.verify(token, jwtSecret, (err: any, user: any) => {
    if (err) {
      return res.status(403).json({ message: 'Invalid or expired token' });
    }
    (req as any).user = user;
    next();
  });
}

export default router;

