import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

// ðŸ”§ ENHANCED SERVER STABILITY - EndgÃ¼ltige LÃ¶sung fÃ¼r Server-Probleme!
process.on('uncaughtException', (error) => {
  console.error('ðŸš¨ [SERVER-RECOVERY] Uncaught Exception:', error.message);
  // Log but don't crash - keep server running for production stability
});

process.on('unhandledRejection', (reason, promise) => {
  console.error('ðŸš¨ [SERVER-RECOVERY] Unhandled Rejection:', reason);
  // Log but don't crash - keep server running for production stability
});

// ðŸ”§ Graceful shutdown handling - PRODUCTION READY
let httpServer: any = null;

process.on('SIGTERM', () => {
  console.log('ðŸ“¡ [SERVER] Received SIGTERM, graceful shutdown...');
  gracefulShutdown();
});

process.on('SIGINT', () => {
  console.log('ðŸ“¡ [SERVER] Received SIGINT, graceful shutdown...');
  gracefulShutdown();
});

const gracefulShutdown = () => {
  if (httpServer) {
    console.log('ðŸ”„ [SERVER] Closing HTTP server gracefully...');
    httpServer.close((err: any) => {
      if (err) {
        console.error('âŒ [SERVER] Error during graceful shutdown:', err);
        process.exit(1);
      } else {
        console.log('âœ… [SERVER] Graceful shutdown completed');
        process.exit(0);
      }
    });

    // Force exit after 10 seconds if graceful shutdown takes too long
    setTimeout(() => {
      console.error('â° [SERVER] Force exit - graceful shutdown timeout');
      process.exit(1);
    }, 10000);
  } else {
    process.exit(0);
  }
};

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client
  const port = 5000;

  // ðŸ”§ ENHANCED SERVER STARTUP mit Retry-Logic - PRODUCTION READY
  const startServer = () => {
    try {
      const serverInstance = server.listen({
        port,
        host: "0.0.0.0",
        reusePort: true,
      }, () => {
        log(`ðŸš€ [SERVER-STABLE] serving on port ${port}`);
        console.log(`âœ… [SERVER-HEALTH] Server successfully started at ${new Date().toISOString()}`);
      });

      // Store server reference for graceful shutdown
      httpServer = serverInstance;

      // Remove existing error listeners to prevent duplicates
      serverInstance.removeAllListeners('error');

      serverInstance.on('error', (error: any) => {
        if (error.code === 'EADDRINUSE') {
          console.error(`ðŸš¨ [SERVER] Port ${port} in use, retrying in 2 seconds...`);
          setTimeout(() => {
            serverInstance.close();
            startServer();
          }, 2000);
        } else {
          console.error('ðŸš¨ [SERVER-ERROR]:', error);
        }
      });

    } catch (error) {
      console.error('ðŸš¨ [SERVER-STARTUP-ERROR]:', error);
      // Retry after 3 seconds
      setTimeout(startServer, 3000);
    }
  };

  startServer();
})();

