import { drizzle } from "drizzle-orm/neon-http";
import { neon } from "@neondatabase/serverless";
import { eq } from "drizzle-orm";
import { 
  users, 
  gameHistory,
  handHistory,
  transactions,
  tournaments,
  tournamentPlayers,
  type User, 
  type InsertUser,
  type GameHistory,
  type HandHistory,
  type Transaction,
  type Tournament,
  type TournamentPlayer
} from "@shared/schema";

// Database connection
const sql = neon(process.env.DATABASE_URL!);
export const db = drizzle(sql);

export interface IStorage {
  // User management
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUserBalance(userId: number, newBalance: string): Promise<void>;
  updateUserStats(userId: number, stats: Partial<User>): Promise<void>;

  // Game history
  createGameHistory(game: Omit<GameHistory, 'id' | 'startedAt'>): Promise<GameHistory>;
  getGameHistory(userId: number, limit?: number): Promise<GameHistory[]>;
  updateGameHistory(gameId: number, updates: Partial<GameHistory>): Promise<void>;

  // Hand history
  createHandHistory(hand: Omit<HandHistory, 'id' | 'timestamp'>): Promise<HandHistory>;
  getHandHistory(gameId: number): Promise<HandHistory[]>;
  getUserHandHistory(userId: number, limit?: number): Promise<HandHistory[]>;

  // Transactions
  createTransaction(transaction: Omit<Transaction, 'id' | 'createdAt'>): Promise<Transaction>;
  getUserTransactions(userId: number, limit?: number): Promise<Transaction[]>;

  // Tournaments
  createTournament(tournament: Omit<Tournament, 'id' | 'createdAt'>): Promise<Tournament>;
  getTournaments(status?: string): Promise<Tournament[]>;
  updateTournament(tournamentId: number, updates: Partial<Tournament>): Promise<void>;
  joinTournament(tournamentId: number, userId: number): Promise<TournamentPlayer>;
}

export class DatabaseStorage implements IStorage {
  // User management
  async getUser(id: number): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.email, email)).limit(1);
    return result[0];
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const result = await db.insert(users).values({
      ...insertUser,
      balance: "1000.00", // Starting balance
      totalWinnings: "0.00",
      totalLosses: "0.00",
      handsPlayed: 0,
      handsWon: 0,
      gamesPlayed: 0,
      gamesWon: 0,
    }).returning();
    return result[0];
  }

  async updateUserBalance(userId: number, newBalance: string): Promise<void> {
    await db.update(users)
      .set({ balance: newBalance, updatedAt: new Date() })
      .where(eq(users.id, userId));
  }

  async updateUserStats(userId: number, stats: Partial<User>): Promise<void> {
    await db.update(users)
      .set({ ...stats, updatedAt: new Date() })
      .where(eq(users.id, userId));
  }

  // Game history
  async createGameHistory(game: Omit<GameHistory, 'id' | 'startedAt'>): Promise<GameHistory> {
    const result = await db.insert(gameHistory).values(game).returning();
    return result[0];
  }

  async getGameHistory(userId: number, limit = 50): Promise<GameHistory[]> {
    return await db.select().from(gameHistory)
      .where(eq(gameHistory.winnerId, userId))
      .orderBy(gameHistory.startedAt)
      .limit(limit);
  }

  async updateGameHistory(gameId: number, updates: Partial<GameHistory>): Promise<void> {
    await db.update(gameHistory)
      .set(updates)
      .where(eq(gameHistory.id, gameId));
  }

  // Hand history
  async createHandHistory(hand: Omit<HandHistory, 'id' | 'timestamp'>): Promise<HandHistory> {
    const result = await db.insert(handHistory).values(hand).returning();
    return result[0];
  }

  async getHandHistory(gameId: number): Promise<HandHistory[]> {
    return await db.select().from(handHistory)
      .where(eq(handHistory.gameId, gameId))
      .orderBy(handHistory.handNumber);
  }

  async getUserHandHistory(userId: number, limit = 100): Promise<HandHistory[]> {
    return await db.select().from(handHistory)
      .where(eq(handHistory.playerId, userId))
      .orderBy(handHistory.timestamp)
      .limit(limit);
  }

  // Transactions
  async createTransaction(transaction: Omit<Transaction, 'id' | 'createdAt'>): Promise<Transaction> {
    const result = await db.insert(transactions).values(transaction).returning();
    return result[0];
  }

  async getUserTransactions(userId: number, limit = 50): Promise<Transaction[]> {
    return await db.select().from(transactions)
      .where(eq(transactions.userId, userId))
      .orderBy(transactions.createdAt)
      .limit(limit);
  }

  // Tournaments
  async createTournament(tournament: Omit<Tournament, 'id' | 'createdAt'>): Promise<Tournament> {
    const result = await db.insert(tournaments).values(tournament).returning();
    return result[0];
  }

  async getTournaments(status?: string): Promise<Tournament[]> {
    if (status) {
      return await db.select().from(tournaments)
        .where(eq(tournaments.status, status))
        .orderBy(tournaments.createdAt);
    }
    return await db.select().from(tournaments).orderBy(tournaments.createdAt);
  }

  async updateTournament(tournamentId: number, updates: Partial<Tournament>): Promise<void> {
    await db.update(tournaments)
      .set(updates)
      .where(eq(tournaments.id, tournamentId));
  }

  async joinTournament(tournamentId: number, userId: number): Promise<TournamentPlayer> {
    const result = await db.insert(tournamentPlayers).values({
      tournamentId,
      userId,
      chipCount: 1000, // Starting chips
      isEliminated: false,
    }).returning();
    return result[0];
  }
}

export const storage = new DatabaseStorage();

