import { pgTable, text, serial, integer, boolean, timestamp, decimal, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  email: text("email").unique(),
  balance: decimal("balance", { precision: 10, scale: 2 }).default("0.00").notNull(),
  totalWinnings: decimal("total_winnings", { precision: 12, scale: 2 }).default("0.00").notNull(),
  totalLosses: decimal("total_losses", { precision: 12, scale: 2 }).default("0.00").notNull(),
  handsPlayed: integer("hands_played").default(0).notNull(),
  handsWon: integer("hands_won").default(0).notNull(),
  gamesPlayed: integer("games_played").default(0).notNull(),
  gamesWon: integer("games_won").default(0).notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const gameHistory = pgTable("game_history", {
  id: serial("id").primaryKey(),
  roomId: text("room_id").notNull(),
  roomName: text("room_name").notNull(),
  playerIds: jsonb("player_ids").notNull(), // Array of player IDs
  playerNames: jsonb("player_names").notNull(), // Array of player names
  winnerId: integer("winner_id"),
  winnerName: text("winner_name"),
  pot: decimal("pot", { precision: 10, scale: 2 }).notNull(),
  blinds: jsonb("blinds").notNull(), // {small: number, big: number}
  duration: integer("duration"), // Game duration in seconds
  rounds: integer("rounds").default(1).notNull(),
  startedAt: timestamp("started_at").defaultNow().notNull(),
  endedAt: timestamp("ended_at"),
});

export const handHistory = pgTable("hand_history", {
  id: serial("id").primaryKey(),
  gameId: integer("game_id").references(() => gameHistory.id),
  handNumber: integer("hand_number").notNull(),
  playerId: integer("player_id"),
  playerName: text("player_name").notNull(),
  holeCards: jsonb("hole_cards").notNull(), // Player's 2 cards
  communityCards: jsonb("community_cards").notNull(), // 5 community cards
  finalHand: text("final_hand"), // Best hand description
  actions: jsonb("actions").notNull(), // Array of actions taken
  finalBet: decimal("final_bet", { precision: 10, scale: 2 }).default("0.00").notNull(),
  winAmount: decimal("win_amount", { precision: 10, scale: 2 }).default("0.00").notNull(),
  isWinner: boolean("is_winner").default(false).notNull(),
  isFolded: boolean("is_folded").default(false).notNull(),
  timestamp: timestamp("timestamp").defaultNow().notNull(),
});

export const transactions = pgTable("transactions", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id).notNull(),
  type: text("type").notNull(), // 'deposit', 'withdrawal', 'win', 'loss', 'buyin'
  amount: decimal("amount", { precision: 10, scale: 2 }).notNull(),
  description: text("description"),
  stripeTransactionId: text("stripe_transaction_id"),
  status: text("status").default("completed").notNull(), // 'pending', 'completed', 'failed'
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const tournaments = pgTable("tournaments", {
  id: serial("id").primaryKey(),
  name: text("name").notNull(),
  buyIn: decimal("buy_in", { precision: 10, scale: 2 }).notNull(),
  prizePool: decimal("prize_pool", { precision: 12, scale: 2 }).default("0.00").notNull(),
  maxPlayers: integer("max_players").notNull(),
  currentPlayers: integer("current_players").default(0).notNull(),
  status: text("status").default("waiting").notNull(), // 'waiting', 'active', 'completed'
  blindStructure: jsonb("blind_structure").notNull(), // Array of blind levels
  currentBlindLevel: integer("current_blind_level").default(0).notNull(),
  winnerId: integer("winner_id"),
  startedAt: timestamp("started_at"),
  endedAt: timestamp("ended_at"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const tournamentPlayers = pgTable("tournament_players", {
  id: serial("id").primaryKey(),
  tournamentId: integer("tournament_id").references(() => tournaments.id).notNull(),
  userId: integer("user_id").references(() => users.id).notNull(),
  position: integer("position"), // Final ranking
  chipCount: integer("chip_count").default(0).notNull(),
  isEliminated: boolean("is_eliminated").default(false).notNull(),
  eliminatedAt: timestamp("eliminated_at"),
  joinedAt: timestamp("joined_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
  email: true,
});

export const insertGameHistorySchema = createInsertSchema(gameHistory);
export const insertHandHistorySchema = createInsertSchema(handHistory);
export const insertTransactionSchema = createInsertSchema(transactions);
export const insertTournamentSchema = createInsertSchema(tournaments);

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type GameHistory = typeof gameHistory.$inferSelect;
export type HandHistory = typeof handHistory.$inferSelect;
export type Transaction = typeof transactions.$inferSelect;
export type Tournament = typeof tournaments.$inferSelect;
export type TournamentPlayer = typeof tournamentPlayers.$inferSelect;

