import React, { useEffect } from 'react';
import { usePoker } from './lib/stores/usePoker';
import { useLanguage } from './lib/stores/useLanguage';
import { socketClient } from './lib/socketClient';
import Lobby from './components/poker/Lobby';
import PokerTable from './components/poker/PokerTable';
import GameUI from './components/poker/GameUI';
import { LanguageSwitcher } from './components/ui/LanguageSwitcher';
import '@fontsource/inter';
import './index.css';

function App() {
  const { 
    showLobby, 
    currentRoom, 
    gameState,
    setConnected,
    setRooms,
    setCurrentRoom,
    setGameState,
    setPlayers,
    setMyCards,
    setShowLobby
  } = usePoker();

  const { t } = useLanguage();

  useEffect(() => {
    // Initialize socket connection
    const initializeSocket = async () => {
      try {
        await socketClient.connect();
        setConnected(true);

        // Set up socket event listeners
        socketClient.onRoomsList((rooms) => {
          setRooms(rooms);
        });

        socketClient.onJoinedRoom((data) => {
          setCurrentRoom(data.room);
          setPlayers(data.players);
          if (data.gameState) {
            setGameState(data.gameState);
          }
        });

        socketClient.onPlayerJoined((data) => {
          setCurrentRoom(data.room);
        });

        socketClient.onPlayerLeft((data) => {
          setCurrentRoom(data.room);
        });

        socketClient.onGameStarted((gameState) => {
          setGameState(gameState);
        });

        socketClient.onGameUpdated((gameState) => {
          setGameState(gameState);
        });

        socketClient.onPlayerAction((data) => {
          console.log('Player action received:', data.action);
          setGameState(data.gameState);
        });

        socketClient.onPrivateCards((cards) => {
          setMyCards(cards);
        });

        socketClient.onError((error) => {
          console.error('Socket error:', error);
          alert(error.message);
        });

      } catch (error) {
        console.error('Failed to connect to server:', error);
        setConnected(false);
      }
    };

    initializeSocket();

    // Cleanup on unmount
    return () => {
      socketClient.disconnect();
    };
  }, [setConnected, setRooms, setCurrentRoom, setGameState, setPlayers, setMyCards]);

  // Handle leaving room
  const handleLeaveRoom = () => {
    socketClient.leaveRoom();
    setCurrentRoom(null);
    setGameState(null);
    setMyCards([]);
    setShowLobby(true);
  };

  if (showLobby || !currentRoom) {
    return (
      <div className="relative">
        {/* Language switcher in top right */}
        <div className="absolute top-4 right-4 z-50">
          <LanguageSwitcher />
        </div>
        <Lobby />
      </div>
    );
  }

  return (
    <div className="w-full h-screen relative overflow-hidden">
      {/* Language switcher and back to lobby button */}
      <div className="absolute top-4 right-4 z-50 flex items-center gap-3">
        <LanguageSwitcher />
        <button
          onClick={handleLeaveRoom}
          className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
        >
          {t('leaveRoom')}
        </button>
      </div>

      {/* Poker table */}
      <PokerTable />

      {/* Game controls */}
      {gameState && gameState.isStarted && (
        <GameUI />
      )}

      {/* Room info and start game button */}
      {currentRoom && (!gameState || !gameState.isStarted) && (
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-black bg-opacity-90 rounded-lg p-6 text-white text-center">
          <h2 className="text-2xl font-bold mb-4">{currentRoom.name}</h2>
          <div className="mb-4">
            <div>{t('players')}: {currentRoom.currentPlayers}/{currentRoom.maxPlayers}</div>
            <div>{t('buyIn').replace(' ($)', '')}: ${currentRoom.buyIn?.toLocaleString()}</div>
            <div>{t('blinds')}: ${currentRoom.blinds?.small}/${currentRoom.blinds?.big}</div>
          </div>

          {currentRoom.currentPlayers >= 3 && (
            <button
              onClick={() => socketClient.startGame()}
              className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg font-bold transition-colors"
            >
              {t('startGame')}
            </button>
          )}

          {currentRoom.currentPlayers < 3 && (
            <div className="text-gray-400">
              {t('waitingForGame')}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;

