import { storage } from '../storage';
import { GameState, Player, Card } from '@shared/types';

export class GameStatsTracker {
  private gameStartTime: Date | null = null;
  private currentGameId: number | null = null;
  private handNumber = 0;
  private playerActions: Map<string, any[]> = new Map();

  async startGame(gameState: GameState, roomName: string): Promise<void> {
    this.gameStartTime = new Date();
    this.handNumber = 0;
    this.playerActions.clear();

    try {
      // Create game history record
      const gameHistory = await storage.createGameHistory({
        roomId: gameState.id,
        roomName,
        playerIds: gameState.players.map(p => p.id),
        playerNames: gameState.players.map(p => p.name),
        winnerId: null,
        winnerName: null,
        pot: gameState.pot.toString(),
        blinds: gameState.blinds,
        duration: null,
        rounds: gameState.round,
        endedAt: null,
      });

      this.currentGameId = gameHistory.id;
    } catch (error) {
      console.error('Error starting game stats tracking:', error);
    }
  }

  async recordPlayerAction(playerId: string, action: any): Promise<void> {
    if (!this.playerActions.has(playerId)) {
      this.playerActions.set(playerId, []);
    }

    const actions = this.playerActions.get(playerId)!;
    actions.push({
      ...action,
      timestamp: new Date(),
      handNumber: this.handNumber,
    });
  }

  async recordHand(gameState: GameState, winners: any[]): Promise<void> {
    if (!this.currentGameId) return;

    this.handNumber++;

    try {
      // Record hand history for each player
      for (const player of gameState.players) {
        const isWinner = winners.some(w => w.playerId === player.id);
        const winAmount = winners.find(w => w.playerId === player.id)?.amount || 0;
        const playerActions = this.playerActions.get(player.id) || [];

        // Only record hand history if we have a valid user ID
        if (player.userId) {
          await storage.createHandHistory({
            gameId: this.currentGameId,
            handNumber: this.handNumber,
            playerId: player.userId,
            playerName: player.name,
            holeCards: player.cards,
            communityCards: gameState.communityCards,
            finalHand: isWinner ? winners.find(w => w.playerId === player.id)?.hand : null,
            actions: playerActions.filter(a => a.handNumber === this.handNumber),
            finalBet: player.totalBet.toString(),
            winAmount: winAmount.toString(),
            isWinner,
            isFolded: player.isFolded,
          });
        } else {
          console.warn(`Player ${player.name} (${player.id}) has no userId - skipping hand history`);
        }

        // Update player statistics
        if (player.userId) {
          const currentUser = await storage.getUser(player.userId);
          if (currentUser) {
            await storage.updateUserStats(player.userId, {
              handsPlayed: currentUser.handsPlayed + 1,
              handsWon: currentUser.handsWon + (isWinner ? 1 : 0),
              totalWinnings: (parseFloat(currentUser.totalWinnings) + (isWinner ? winAmount : 0)).toString(),
              totalLosses: (parseFloat(currentUser.totalLosses) + (isWinner ? 0 : player.totalBet)).toString(),
            });
          }
        }
      }

      // Clear actions for this hand
      this.playerActions.clear();
    } catch (error) {
      console.error('Error recording hand history:', error);
    }
  }

  async endGame(gameState: GameState, winners: any[]): Promise<void> {
    if (!this.currentGameId || !this.gameStartTime) return;

    try {
      const endTime = new Date();
      const duration = Math.floor((endTime.getTime() - this.gameStartTime.getTime()) / 1000);

      const mainWinner = winners[0];

      await storage.updateGameHistory(this.currentGameId, {
        winnerId: mainWinner ? 
          gameState.players.find(p => p.id === mainWinner.playerId)?.userId || null : null,
        winnerName: mainWinner?.playerId ? 
          gameState.players.find(p => p.id === mainWinner.playerId)?.name : null,
        pot: gameState.pot.toString(),
        duration,
        rounds: gameState.round,
        endedAt: endTime,
      });

      // Update game statistics for all players
      for (const player of gameState.players) {
        if (player.userId) {
          const currentUser = await storage.getUser(player.userId);
          if (currentUser) {
            const isGameWinner = winners.some(w => w.playerId === player.id);
            await storage.updateUserStats(player.userId, {
              gamesPlayed: currentUser.gamesPlayed + 1,
              gamesWon: currentUser.gamesWon + (isGameWinner ? 1 : 0),
            });
          }
        } else {
          console.warn(`Player ${player.name} (${player.id}) has no userId - skipping game stats update`);
        }
      }

      // Log winnings for tracking (no real money involved)
      for (const winner of winners) {
        const winnerPlayer = gameState.players.find(p => p.id === winner.playerId);
        if (winnerPlayer?.userId) {
          try {
            // Create transaction record for tracking only
            await storage.createTransaction({
              userId: winnerPlayer.userId,
              type: 'win',
              amount: winner.amount.toString(),
              description: `Won $${winner.amount} in poker game`,
              stripeTransactionId: null,
              status: 'completed',
            });

            console.log(`Player ${winnerPlayer.name} (ID: ${winnerPlayer.userId}) won $${winner.amount} (tracking only)`);
          } catch (error) {
            console.error(`Error logging winnings for player ${winnerPlayer.name}:`, error);
          }
        } else {
          console.warn(`Winner player ${winner.playerId} has no userId - skipping tracking`);
        }
      }

      // Reset tracking
      this.currentGameId = null;
      this.gameStartTime = null;
      this.handNumber = 0;
      this.playerActions.clear();
    } catch (error) {
      console.error('Error ending game stats tracking:', error);
    }
  }

  getCurrentGameId(): number | null {
    return this.currentGameId;
  }

  getCurrentHandNumber(): number {
    return this.handNumber;
  }
}

server/poker/handEvaluator.ts.content:
import { Card, HandRanking } from '@shared/types';

export class HandEvaluator {
  private static readonly HAND_RANKINGS = {
    HIGH_CARD: 1,
    PAIR: 2,
    TWO_PAIR: 3,
    THREE_OF_A_KIND: 4,
    STRAIGHT: 5,
    FLUSH: 6,
    FULL_HOUSE: 7,
    FOUR_OF_A_KIND: 8,
    STRAIGHT_FLUSH: 9,
    ROYAL_FLUSH: 10
  };

  private static getCardValue(rank: string): number {
    const values: { [key: string]: number } = {
      'A': 14, 'K': 13, 'Q': 12, 'J': 11, '10': 10,
      '9': 9, '8': 8, '7': 7, '6': 6, '5': 5, '4': 4, '3': 3, '2': 2
    };
    return values[rank];
  }

  private static isAceLowStraight(values: number[]): boolean {
    const sortedValues = [...values].sort((a, b) => a - b);
    return JSON.stringify(sortedValues) === JSON.stringify([2, 3, 4, 5, 14]);
  }

  public static evaluateHand(cards: Card[]): HandRanking {
    if (cards.length !== 7) {
      throw new Error('Hand evaluation requires exactly 7 cards');
    }

    // Get all possible 5-card combinations
    const combinations = this.getCombinations(cards, 5);
    let bestHand: HandRanking = {
      rank: 0,
      name: '',
      cards: [],
      tiebreakers: []
    };

    for (const combo of combinations) {
      const handRank = this.evaluateFiveCards(combo);
      if (handRank.rank > bestHand.rank || 
          (handRank.rank === bestHand.rank && this.compareTiebreakers(handRank.tiebreakers, bestHand.tiebreakers) > 0)) {
        bestHand = handRank;
      }
    }

    return bestHand;
  }

  private static evaluateFiveCards(cards: Card[]): HandRanking {
    const values = cards.map(card => this.getCardValue(card.rank)).sort((a, b) => b - a);
    const suits = cards.map(card => card.suit);

    const isFlush = suits.every(suit => suit === suits[0]);
    const isStraight = this.isStraight(values);
    const isRoyalFlush = isFlush && isStraight && values[0] === 14 && values[4] === 10;
    const isStraightFlush = isFlush && isStraight;

    // Count occurrences of each value
    const valueCounts: { [key: number]: number } = {};
    values.forEach(value => {
      valueCounts[value] = (valueCounts[value] || 0) + 1;
    });

    const counts = Object.values(valueCounts).sort((a, b) => b - a);
    const countValues = Object.keys(valueCounts)
      .map(k => parseInt(k))
      .sort((a, b) => (valueCounts[b] - valueCounts[a]) || (b - a));

    if (isRoyalFlush) {
      return {
        rank: this.HAND_RANKINGS.ROYAL_FLUSH,
        name: 'Royal Flush',
        cards,
        tiebreakers: []
      };
    }

    if (isStraightFlush) {
      const highCard = this.isAceLowStraight(values) ? 5 : values[0];
      return {
        rank: this.HAND_RANKINGS.STRAIGHT_FLUSH,
        name: 'Straight Flush',
        cards,
        tiebreakers: [highCard]
      };
    }

    if (counts[0] === 4) {
      return {
        rank: this.HAND_RANKINGS.FOUR_OF_A_KIND,
        name: 'Four of a Kind',
        cards,
        tiebreakers: [countValues[0], countValues[1]]
      };
    }

    if (counts[0] === 3 && counts[1] === 2) {
      return {
        rank: this.HAND_RANKINGS.FULL_HOUSE,
        name: 'Full House',
        cards,
        tiebreakers: [countValues[0], countValues[1]]
      };
    }

    if (isFlush) {
      return {
        rank: this.HAND_RANKINGS.FLUSH,
        name: 'Flush',
        cards,
        tiebreakers: values
      };
    }

    if (isStraight) {
      const highCard = this.isAceLowStraight(values) ? 5 : values[0];
      return {
        rank: this.HAND_RANKINGS.STRAIGHT,
        name: 'Straight',
        cards,
        tiebreakers: [highCard]
      };
    }

    if (counts[0] === 3) {
      return {
        rank: this.HAND_RANKINGS.THREE_OF_A_KIND,
        name: 'Three of a Kind',
        cards,
        tiebreakers: [countValues[0], ...countValues.slice(1)]
      };
    }

    if (counts[0] === 2 && counts[1] === 2) {
      return {
        rank: this.HAND_RANKINGS.TWO_PAIR,
        name: 'Two Pair',
        cards,
        tiebreakers: [countValues[0], countValues[1], countValues[2]]
      };
    }

    if (counts[0] === 2) {
      return {
        rank: this.HAND_RANKINGS.PAIR,
        name: 'Pair',
        cards,
        tiebreakers: [countValues[0], ...countValues.slice(1)]
      };
    }

    return {
      rank: this.HAND_RANKINGS.HIGH_CARD,
      name: 'High Card',
      cards,
      tiebreakers: values
    };
  }

  private static isStraight(values: number[]): boolean {
    const uniqueValues = Array.from(new Set(values)).sort((a, b) => b - a);

    if (uniqueValues.length !== 5) return false;

    // Check for ace-low straight (A, 5, 4, 3, 2)
    if (this.isAceLowStraight(uniqueValues)) return true;

    // Check for regular straight
    for (let i = 0; i < 4; i++) {
      if (uniqueValues[i] - uniqueValues[i + 1] !== 1) return false;
    }

    return true;
  }

  private static compareTiebreakers(a: number[], b: number[]): number {
    for (let i = 0; i < Math.max(a.length, b.length); i++) {
      const valA = a[i] || 0;
      const valB = b[i] || 0;
      if (valA !== valB) return valA - valB;
    }
    return 0;
  }

  private static getCombinations<T>(arr: T[], k: number): T[][] {
    if (k === 1) return arr.map(x => [x]);
    if (k === arr.length) return [arr];

    const result: T[][] = [];
    for (let i = 0; i <= arr.length - k; i++) {
      const first = arr[i];
      const rest = arr.slice(i + 1);
      const combos = this.getCombinations(rest, k - 1);
      combos.forEach(combo => result.push([first, ...combo]));
    }
    return result;
  }

  public static compareHands(hand1: HandRanking, hand2: HandRanking): number {
    if (hand1.rank !== hand2.rank) {
      return hand1.rank - hand2.rank;
    }
    return this.compareTiebreakers(hand1.tiebreakers, hand2.tiebreakers);
  }
}

