import { Server, Socket } from 'socket.io';
import { PokerGame } from './gameLogic';
import { BotPlayer } from './botPlayer';
import { GameStatsTracker } from './gameStatsTracker';
import { TournamentEngine } from './tournamentEngine';
import { storage } from '../storage';
import { verifyToken } from '../auth';
import { Room, Player, PokerAction, TournamentBlindLevel } from '@shared/types';

interface GameRoom {
  room: Room;
  game?: PokerGame;
  statsTracker?: GameStatsTracker;
  players: Map<string, Player>;
}

export class PokerSocketHandler {
  private io: Server;
  private rooms: Map<string, GameRoom> = new Map();
  private playerToRoom: Map<string, string> = new Map();
  private socketToUserId: Map<string, string> = new Map();
  private socketToUsername: Map<string, string> = new Map();
  private tournamentEngine: TournamentEngine;

  constructor(io: Server) {
    this.io = io;
    this.tournamentEngine = new TournamentEngine();
    this.setupSocketHandlers();
  }

  private setupSocketHandlers(): void {
    this.io.on('connection', (socket: Socket) => {
      console.log(`Player connected: ${socket.id}`);

      // Handle user authentication
      socket.on('authenticate', async (data: { token?: string; userId?: number; username?: string }) => {
        try {
          if (data.token) {
            // Verify JWT token
            const decoded = verifyToken(data.token);
            if (!decoded) {
              socket.emit('authenticated', { success: false, message: 'Invalid token' });
              return;
            }

            // Get user from database to verify they exist and get current info
            const user = await storage.getUser(parseInt(decoded.userId));
            if (!user) {
              socket.emit('authenticated', { success: false, message: 'User not found' });
              return;
            }

            // Store authenticated user mapping  
            this.socketToUserId.set(socket.id, user.id.toString());
            this.socketToUsername.set(socket.id, user.username);

            socket.emit('authenticated', { 
              success: true, 
              user: { 
                id: user.id, 
                username: user.username, 
                balance: user.balance 
              } 
            });
            console.log(`Socket ${socket.id} authenticated as user ${user.id} (${user.username})`);
          } else {
            // Reject all non-token authentication for security
            socket.emit('authenticated', { success: false, message: 'Authentication token required for real money games' });
            console.log(`Authentication rejected for socket ${socket.id} - no valid token provided`);
          }
        } catch (error) {
          console.error('Authentication error:', error);
          socket.emit('authenticated', { success: false, message: 'Authentication failed' });
        }
      });

      socket.on('get-rooms', () => {
        this.handleGetRooms(socket);
      });

      socket.on('create-room', (data) => {
        this.handleCreateRoom(socket, data);
      });

      socket.on('create-singleplayer-room', (data) => {
        this.handleCreateSinglePlayerRoom(socket, data);
      });

      socket.on('join-room', (data) => {
        this.handleJoinRoom(socket, data);
      });

      socket.on('leave-room', () => {
        this.handleLeaveRoom(socket);
      });

      socket.on('start-game', () => {
        this.handleStartGame(socket);
      });

      socket.on('player-action', (action: PokerAction) => {
        this.handlePlayerAction(socket, action);
      });

      // Tournament handlers
      socket.on('get-tournaments', () => {
        this.handleGetTournaments(socket);
      });

      socket.on('create-tournament', (data) => {
        this.handleCreateTournament(socket, data);
      });

      socket.on('join-tournament', (data) => {
        this.handleJoinTournament(socket, data);
      });

      socket.on('start-tournament', (data) => {
        this.handleStartTournament(socket, data);
      });

      socket.on('disconnect', () => {
        this.handleDisconnect(socket);
      });
    });
  }

  private handleGetRooms(socket: Socket): void {
    const publicRooms = Array.from(this.rooms.values())
      .filter(gameRoom => !gameRoom.room.isPrivate)
      .map(gameRoom => ({
        ...gameRoom.room,
        currentPlayers: gameRoom.players.size
      }));

    socket.emit('rooms-list', publicRooms);
  }

  private async handleCreateRoom(socket: Socket, data: {
    name: string;
    maxPlayers: number;
    blinds: { small: number; big: number };
    buyIn: number;
    isPrivate: boolean;
    password?: string;
    playerName: string;
  }): Promise<void> {
    // Require authentication for real money games
    const userId = this.socketToUserId.get(socket.id);
    if (!userId) {
      socket.emit('error', { message: 'Authentication required to create real money games' });
      return;
    }
    const roomId = this.generateRoomId();
    const room: Room = {
      id: roomId,
      name: data.name,
      maxPlayers: Math.min(Math.max(data.maxPlayers, 3), 6),
      currentPlayers: 0,
      blinds: data.blinds,
      buyIn: data.buyIn,
      isPrivate: data.isPrivate,
      password: data.password
    };

    const gameRoom: GameRoom = {
      room,
      players: new Map()
    };

    this.rooms.set(roomId, gameRoom);

    // Add creator to room
    await this.joinPlayerToRoom(socket, roomId, data.playerName, data.buyIn);

    console.log(`[DEBUG] Emitting room-created event for room ${roomId} to socket ${socket.id}`);
    socket.emit('room-created', { roomId, room: this.getRoomData(gameRoom) });
    console.log(`[DEBUG] room-created event emitted for room ${roomId}`);
  }

  private async handleCreateSinglePlayerRoom(socket: Socket, data: {
    name: string;
    blinds: { small: number; big: number };
    buyIn: number;
    playerName: string;
    botCount?: number;
  }): Promise<void> {
    // Require authentication for single player games too
    const userId = this.socketToUserId.get(socket.id);
    if (!userId) {
      socket.emit('error', { message: 'Authentication required to create games' });
      return;
    }

    // Look for existing single player room
    const existingSinglePlayerRoom = this.findAvailableSinglePlayerRoom();

    if (existingSinglePlayerRoom) {
      console.log(`[DEBUG] Reusing existing single player room ${existingSinglePlayerRoom.room.id}`);

      // Reset the room completely for reuse
      await this.resetSinglePlayerRoom(existingSinglePlayerRoom, data, socket);

      socket.emit('room-created', { roomId: existingSinglePlayerRoom.room.id, room: this.getRoomData(existingSinglePlayerRoom) });
      return;
    }

    // Create new single player room
    const roomId = this.generateRoomId();
    const botCount = data.botCount || 3; // Default to 3 bots (4 total players)
    const maxPlayers = botCount + 1; // Human player + bots

    const room: Room = {
      id: roomId,
      name: "Single Player vs Computer",
      maxPlayers,
      currentPlayers: 0,
      blinds: data.blinds,
      buyIn: data.buyIn,
      isPrivate: false,
      isSinglePlayer: true
    };

    // Create bots for the game
    const bots = BotPlayer.createBots(botCount, data.buyIn);
    const botPlayers = bots.map((bot, index) => bot.createPlayerObject(data.buyIn, index + 1));

    const gameRoom: GameRoom = {
      room,
      players: new Map()
    };

    this.rooms.set(roomId, gameRoom);

    // Add bot players to the room FIRST
    botPlayers.forEach(botPlayer => {
      gameRoom.players.set(botPlayer.id, botPlayer);
    });

    // Add human player to room (this will include bots in the joined-room payload)
    await this.joinPlayerToRoom(socket, roomId, data.playerName, data.buyIn);

    // Update room player count
    gameRoom.room.currentPlayers = gameRoom.players.size;

    // Create the poker game with bots
    const allPlayers = Array.from(gameRoom.players.values());
    gameRoom.game = new PokerGame(roomId, allPlayers, room.blinds, bots);

    // Set up bot action callback
    gameRoom.game.setBotActionCallback((action: PokerAction) => {
      console.log(`[BOT ACTION] ${action.playerId}: ${action.type}${action.amount ? ` $${action.amount}` : ''}`);

      // Safety check - ensure game still exists
      if (!gameRoom.game) {
        console.log(`[DEBUG] Bot action callback fired but game is undefined, ignoring`);
        return;
      }

      // Broadcast bot action to the room
      this.io.to(roomId).emit('player-action', {
        action,
        gameState: gameRoom.game.getGameState()
      });

      // Check if game needs another bot action  
      if (gameRoom.game.needsBotAction()) {
        setTimeout(() => {
          // Double-check game still exists before processing
          if (gameRoom.game) {
            gameRoom.game.processBotAction();
          }
        }, 1000);
      }
    });

    // ðŸŽ° Set up Bojek Poker Ironic Event Callback! ðŸŽ²
    gameRoom.game.setEventCallback((eventData: any) => {
      console.log(`[BOJEK POKER EVENT] ${eventData.event?.name}: ${eventData.event?.description}`);

      // Safety check - ensure game still exists
      if (!gameRoom.game) {
        console.log(`[DEBUG] Ironic event callback fired but game is undefined, ignoring`);
        return;
      }

      // Broadcast the amazing ironic event to all players! ðŸŽ°
      this.io.to(roomId).emit('ironic-event', eventData);

      // Also broadcast updated game state with the new event data
      this.io.to(roomId).emit('game-updated', gameRoom.game.getGameState());
    });

    // Auto-start the single player game immediately
    console.log(`[DEBUG] Auto-starting single player game in room ${roomId}`);
    try {
      const gameState = gameRoom.game.startGame();
      gameRoom.statsTracker = new GameStatsTracker();
      await gameRoom.statsTracker.startGame(gameState, gameRoom.room.name);

      this.io.to(roomId).emit('game-started', gameState);
      this.sendPrivateGameData(roomId);
      console.log(`[DEBUG] Single player game started automatically in room ${roomId}`);
    } catch (error) {
      console.error('Error auto-starting single player game:', error);
      socket.emit('error', { message: 'Failed to start game automatically' });
    }

    console.log(`Created single player room ${roomId} with ${botCount} bots`);
    socket.emit('room-created', { roomId, room: this.getRoomData(gameRoom) });
  }

  /**
   * Find an available single player room (not started or ended)
   */
  private findAvailableSinglePlayerRoom(): GameRoom | null {
    const roomArray = Array.from(this.rooms.values());
    for (const gameRoom of roomArray) {
      if (gameRoom.room.isSinglePlayer) {
        // Check if room has space for human players (excluding bots)
        const humanPlayers = Array.from(gameRoom.players.values()).filter((p: Player) => !p.id.startsWith('bot_'));
        const gameState = gameRoom.game?.getGameState();

        // Room is available if:
        // 1. Has space for another human player
        // 2. Game hasn't started yet OR has ended (for reset)
        if (humanPlayers.length < 1 && (!gameState || !gameState.isStarted || gameState.phase === 'ended')) {
          return gameRoom;
        }
      }
    }
    return null;
  }

  /**
   * Reset and reinitialize a single player room for reuse
   */
  private async resetSinglePlayerRoom(gameRoom: GameRoom, data: {
    name: string;
    blinds: { small: number; big: number };
    buyIn: number;
    playerName: string;
    botCount?: number;
  }, socket: Socket): Promise<void> {
    const roomId = gameRoom.room.id;
    const botCount = data.botCount || 3;

    // Clear existing players and game state
    gameRoom.players.clear();
    gameRoom.game = undefined;
    gameRoom.statsTracker = undefined;

    // Update room settings
    gameRoom.room.blinds = data.blinds;
    gameRoom.room.buyIn = data.buyIn;
    gameRoom.room.maxPlayers = botCount + 1;

    // Create fresh bots FIRST
    const bots = BotPlayer.createBots(botCount, data.buyIn);
    const botPlayers = bots.map((bot, index) => bot.createPlayerObject(data.buyIn, index + 1));

    // Add bot players to the room first
    botPlayers.forEach(botPlayer => {
      gameRoom.players.set(botPlayer.id, botPlayer);
    });

    // Add human player (this will include bots in the joined-room payload)
    await this.joinPlayerToRoom(socket, roomId, data.playerName, data.buyIn);

    // Update room player count
    gameRoom.room.currentPlayers = gameRoom.players.size;

    // Create fresh poker game with bots
    const allPlayers = Array.from(gameRoom.players.values());
    gameRoom.game = new PokerGame(roomId, allPlayers, gameRoom.room.blinds, bots);

    // Set up bot action callback
    gameRoom.game.setBotActionCallback((action: PokerAction) => {
      console.log(`[BOT ACTION] ${action.playerId}: ${action.type}${action.amount ? ` $${action.amount}` : ''}`);

      // Safety check - ensure game still exists
      if (!gameRoom.game) {
        console.log(`[DEBUG] Bot action callback fired but game is undefined, ignoring`);
        return;
      }

      // Broadcast bot action to the room
      this.io.to(roomId).emit('player-action', {
        action,
        gameState: gameRoom.game.getGameState()
      });

      // Check if game needs another bot action  
      if (gameRoom.game.needsBotAction()) {
        setTimeout(() => {
          // Double-check game still exists before processing
          if (gameRoom.game) {
            gameRoom.game.processBotAction();
          }
        }, 1000);
      }
    });

    // ðŸŽ° Set up Bojek Poker Ironic Event Callback! ðŸŽ²
    gameRoom.game.setEventCallback((eventData: any) => {
      console.log(`[BOJEK POKER EVENT] ${eventData.event?.name}: ${eventData.event?.description}`);

      // Safety check - ensure game still exists
      if (!gameRoom.game) {
        console.log(`[DEBUG] Ironic event callback fired but game is undefined, ignoring`);
        return;
      }

      // Broadcast the amazing ironic event to all players! ðŸŽ°
      this.io.to(roomId).emit('ironic-event', eventData);

      // Also broadcast updated game state with the new event data
      this.io.to(roomId).emit('game-updated', gameRoom.game.getGameState());
    });

    // Auto-start the game
    console.log(`[DEBUG] Auto-starting reused single player game in room ${roomId}`);
    try {
      const gameState = gameRoom.game.startGame();
      gameRoom.statsTracker = new GameStatsTracker();
      await gameRoom.statsTracker.startGame(gameState, gameRoom.room.name);

      this.io.to(roomId).emit('game-started', gameState);
      this.sendPrivateGameData(roomId);
      console.log(`[DEBUG] Reused single player game started automatically in room ${roomId}`);
    } catch (error) {
      console.error('Error auto-starting reused single player game:', error);
      socket.emit('error', { message: 'Failed to start game automatically' });
    }
  }

  private async handleJoinRoom(socket: Socket, data: {
    roomId: string;
    playerName: string;
    buyIn: number;
    password?: string;
  }): Promise<void> {
    console.log(`[DEBUG] handleJoinRoom called for socket ${socket.id} with data:`, JSON.stringify(data));

    // Require authentication for real money games
    const userId = this.socketToUserId.get(socket.id);
    console.log(`[DEBUG] userId for join: ${userId}`);

    if (!userId) {
      console.log(`[DEBUG] No userId for join, emitting error`);
      socket.emit('error', { message: 'Authentication required to join real money games' });
      return;
    }
    const gameRoom = this.rooms.get(data.roomId);

    if (!gameRoom) {
      socket.emit('error', { message: 'Room not found' });
      return;
    }

    if (gameRoom.room.isPrivate && gameRoom.room.password !== data.password) {
      socket.emit('error', { message: 'Invalid password' });
      return;
    }

    if (gameRoom.players.size >= gameRoom.room.maxPlayers) {
      socket.emit('error', { message: 'Room is full' });
      return;
    }

    if (data.buyIn < gameRoom.room.buyIn) {
      socket.emit('error', { message: 'Insufficient buy-in amount' });
      return;
    }

    await this.joinPlayerToRoom(socket, data.roomId, data.playerName, data.buyIn);
  }

  private async joinPlayerToRoom(socket: Socket, roomId: string, playerName: string, buyIn: number): Promise<void> {
    const gameRoom = this.rooms.get(roomId)!;

    // Remove player from previous room if any
    this.handleLeaveRoom(socket);

    // Log buy-in for statistics (no real money involved)
    const userId = this.socketToUserId.get(socket.id);
    if (userId) {
      try {
        // Create buy-in transaction record for tracking only
        await storage.createTransaction({
          userId: parseInt(userId),
          type: 'buyin',
          amount: buyIn.toString(), // Positive for tracking
          description: `Buy-in for poker room: ${gameRoom.room.name}`,
          stripeTransactionId: null,
          status: 'completed',
        });

        console.log(`Player ${playerName} (ID: ${userId}) bought in for $${buyIn} (tracking only)`);
      } catch (error) {
        console.error('Error logging buy-in:', error);
      }
    }

    // For single player rooms, human gets position 0, bots get 1,2,3
    const position = gameRoom.room.isSinglePlayer ? 0 : gameRoom.players.size;

    const player: Player = {
      id: socket.id,
      userId: parseInt(this.socketToUserId.get(socket.id) || '0'),
      name: playerName,
      chips: buyIn,
      cards: [],
      currentBet: 0,
      totalBet: 0,
      position: position,
      isActive: true,
      isFolded: false,
      isAllIn: false
    };

    gameRoom.players.set(socket.id, player);
    this.playerToRoom.set(socket.id, roomId);

    socket.join(roomId);

    // Notify all players in room
    this.io.to(roomId).emit('player-joined', {
      player: this.getPublicPlayerData(player),
      room: this.getRoomData(gameRoom)
    });

    console.log(`[DEBUG] About to emit joined-room event for socket ${socket.id} to room ${roomId}`);
    socket.emit('joined-room', {
      roomId,
      room: this.getRoomData(gameRoom),
      players: Array.from(gameRoom.players.values()).map(p => this.getPublicPlayerData(p)),
      gameState: gameRoom.game?.getGameState()
    });
    console.log(`[DEBUG] joined-room event emitted for socket ${socket.id}`);

  }

  private handleLeaveRoom(socket: Socket): void {
    const roomId = this.playerToRoom.get(socket.id);
    if (!roomId) return;

    const gameRoom = this.rooms.get(roomId);
    if (!gameRoom) return;

    gameRoom.players.delete(socket.id);
    this.playerToRoom.delete(socket.id);
    socket.leave(roomId);

    // If room is empty, delete it
    if (gameRoom.players.size === 0) {
      console.log(`[DEBUG] Deleting empty room ${roomId}`);
      this.rooms.delete(roomId);
      return;
    }

    // For single player rooms, if the human player leaves, reset the room
    if (gameRoom.room.isSinglePlayer) {
      const humanPlayers = Array.from(gameRoom.players.values()).filter((p: Player) => !p.id.startsWith('bot_'));
      if (humanPlayers.length === 0) {
        console.log(`[DEBUG] Resetting single player room ${roomId} - no human players left`);
        // Reset the game state but keep the room for reuse
        gameRoom.game = undefined;
        gameRoom.statsTracker = undefined;
        return;
      }
    }

    // Notify remaining players
    this.io.to(roomId).emit('player-left', {
      playerId: socket.id,
      room: this.getRoomData(gameRoom)
    });

    // If game was in progress and player was active, handle their absence
    if (gameRoom.game) {
      const gameState = gameRoom.game.getGameState();
      const player = gameState.players.find(p => p.id === socket.id);
      if (player && gameState.isStarted && !gameState.winners) {
        // Auto-fold the disconnected player if it's their turn
        if (gameState.currentPlayerIndex === player.position) {
          try {
            const updatedState = gameRoom.game.processAction({
              type: 'fold',
              playerId: socket.id
            });
            this.io.to(roomId).emit('game-updated', updatedState);
          } catch (error) {
            console.error('Error handling disconnected player action:', error);
          }
        }
      }
    }
  }

  private async handleStartGame(socket: Socket): Promise<void> {
    const roomId = this.playerToRoom.get(socket.id);
    if (!roomId) {
      socket.emit('error', { message: 'Not in a room' });
      return;
    }

    const gameRoom = this.rooms.get(roomId)!;
    const players = Array.from(gameRoom.players.values());

    if (players.length < 3) {
      socket.emit('error', { message: 'Need at least 3 players to start' });
      return;
    }

    if (gameRoom.game && gameRoom.game.getGameState().isStarted) {
      socket.emit('error', { message: 'Game already started' });
      return;
    }

    try {
      gameRoom.game = new PokerGame(roomId, players, gameRoom.room.blinds);
      gameRoom.statsTracker = new GameStatsTracker();

      const gameState = gameRoom.game.startGame();

      // Start stats tracking
      await gameRoom.statsTracker.startGame(gameState, gameRoom.room.name);

      this.io.to(roomId).emit('game-started', gameState);
      this.sendPrivateGameData(roomId);
    } catch (error) {
      socket.emit('error', { message: (error as Error).message });
    }
  }

  private async handlePlayerAction(socket: Socket, action: PokerAction): Promise<void> {
    const roomId = this.playerToRoom.get(socket.id);
    if (!roomId) {
      socket.emit('error', { message: 'Not in a room' });
      return;
    }

    const gameRoom = this.rooms.get(roomId)!;
    if (!gameRoom.game) {
      socket.emit('error', { message: 'No active game' });
      return;
    }

    try {
      action.playerId = socket.id;

      // Record player action for stats
      if (gameRoom.statsTracker) {
        await gameRoom.statsTracker.recordPlayerAction(socket.id, action);
      }

      const updatedState = gameRoom.game.processAction(action);

      // Record hand completion if game moved to showdown or ended
      if (updatedState.phase === 'showdown' && gameRoom.statsTracker) {
        await gameRoom.statsTracker.recordHand(updatedState, updatedState.winners || []);
      }

      this.io.to(roomId).emit('game-updated', updatedState);
      this.sendPrivateGameData(roomId);

      // Check if a bot should act next
      if (gameRoom.game.needsBotAction()) {
        setTimeout(() => {
          // Double-check game still exists before processing
          if (gameRoom.game) {
            gameRoom.game.processBotAction();
          }
        }, 500);
      }

      // Check if game ended
      if (updatedState.phase === 'ended') {
        if (gameRoom.statsTracker && updatedState.winners) {
          await gameRoom.statsTracker.endGame(updatedState, updatedState.winners);
        }

        // Start new round after delay if enough players
        if (updatedState.players.filter(p => p.chips > 0).length > 1) {
          setTimeout(() => {
            if (gameRoom.game) {
              const newRoundState = gameRoom.game.nextRound();
              this.io.to(roomId).emit('game-updated', newRoundState);
              this.sendPrivateGameData(roomId);
            }
          }, 5000); // 5 second delay before next round
        }
      }
    } catch (error) {
      socket.emit('error', { message: (error as Error).message });
    }
  }

  private handleDisconnect(socket: Socket): void {
    console.log(`Player disconnected: ${socket.id}`);
    this.handleLeaveRoom(socket);
    this.socketToUserId.delete(socket.id);
  }

  private sendPrivateGameData(roomId: string): void {
    const gameRoom = this.rooms.get(roomId);
    if (!gameRoom || !gameRoom.game) return;

    const gameState = gameRoom.game.getGameState();

    // Send each player their private cards
    gameState.players.forEach(player => {
      this.io.to(player.id).emit('private-cards', player.cards);
    });
  }

  private getRoomData(gameRoom: GameRoom): Room {
    return {
      ...gameRoom.room,
      currentPlayers: gameRoom.players.size,
      gameState: gameRoom.game?.getGameState()
    };
  }

  private getPublicPlayerData(player: Player) {
    return {
      id: player.id,
      name: player.name,
      chips: player.chips,
      position: player.position,
      currentBet: player.currentBet,
      totalBet: player.totalBet,
      isActive: player.isActive,
      isFolded: player.isFolded,
      isAllIn: player.isAllIn,
      lastAction: player.lastAction,
      cardCount: player.cards.length // Don't send actual cards to other players
    };
  }

  private generateRoomId(): string {
    return Math.random().toString(36).substring(2, 8).toUpperCase();
  }

  // Tournament handlers
  private handleGetTournaments(socket: Socket): void {
    const tournaments = this.tournamentEngine.getActiveTournaments();
    socket.emit('tournaments-list', tournaments);
  }

  private async handleCreateTournament(socket: Socket, data: {
    name: string;
    buyIn: number;
    maxPlayers: number;
    blindStructure?: TournamentBlindLevel[];
    playerName: string;
  }): Promise<void> {
    // Require authentication for real money tournaments
    const userId = this.socketToUserId.get(socket.id);
    if (!userId) {
      socket.emit('error', { message: 'Authentication required to create real money tournaments' });
      return;
    }

    try {
      // Log tournament buy-in for statistics (no real money involved)
      try {
        await storage.createTransaction({
          userId: parseInt(userId),
          type: 'buyin',
          amount: data.buyIn.toString(), // Positive for tracking
          description: `Tournament buy-in: ${data.name}`,
          stripeTransactionId: null,
          status: 'completed',
        });

        console.log(`Player ${data.playerName} (ID: ${userId}) tournament buy-in $${data.buyIn} (tracking only)`);
      } catch (error) {
        console.error('Error logging tournament buy-in:', error);
      }

      const tournament = this.tournamentEngine.createTournament(
        data.name,
        data.buyIn,
        Math.min(Math.max(data.maxPlayers, 3), 20),
        data.blindStructure
      );

      // Auto-join the creator
      const joined = this.tournamentEngine.joinTournament(tournament.id, socket.id, data.playerName);

      if (joined) {
        socket.emit('tournament-created', tournament);
        socket.join(`tournament_${tournament.id}`);
        this.io.emit('tournament-updated', tournament);
      } else {
        socket.emit('error', { message: 'Failed to join tournament' });
      }
    } catch (error) {
      socket.emit('error', { message: (error as Error).message });
    }
  }

  private async handleJoinTournament(socket: Socket, data: {
    tournamentId: string;
    playerName: string;
  }): Promise<void> {
    // Require authentication for real money tournaments
    const userId = this.socketToUserId.get(socket.id);
    if (!userId) {
      socket.emit('error', { message: 'Authentication required to join real money tournaments' });
      return;
    }

    try {
      // Get tournament details 
      const tournament = this.tournamentEngine.getTournament(data.tournamentId);
      if (!tournament) {
        socket.emit('error', { message: 'Tournament not found' });
        return;
      }

      // Log tournament join for statistics (no real money involved)
      try {
        await storage.createTransaction({
          userId: parseInt(userId),
          type: 'buyin',
          amount: tournament.buyIn.toString(), // Positive for tracking
          description: `Tournament buy-in: ${tournament.name}`,
          stripeTransactionId: null,
          status: 'completed',
        });

        console.log(`Player ${data.playerName} (ID: ${userId}) tournament buy-in $${tournament.buyIn} (tracking only)`);
      } catch (error) {
        console.error('Error logging tournament buy-in:', error);
      }

      const joined = this.tournamentEngine.joinTournament(data.tournamentId, socket.id, data.playerName);

      if (joined) {
        socket.join(`tournament_${data.tournamentId}`);
        const tournament = this.tournamentEngine.getTournament(data.tournamentId);

        if (tournament) {
          socket.emit('tournament-joined', tournament);
          this.io.to(`tournament_${data.tournamentId}`).emit('tournament-updated', tournament);
        }
      } else {
        socket.emit('error', { message: 'Could not join tournament' });
      }
    } catch (error) {
      socket.emit('error', { message: (error as Error).message });
    }
  }

  private handleStartTournament(socket: Socket, data: { tournamentId: string }): void {
    // Require authentication for tournament management
    const userId = this.socketToUserId.get(socket.id);
    if (!userId) {
      socket.emit('error', { message: 'Authentication required to manage tournaments' });
      return;
    }

    try {
      const started = this.tournamentEngine.startTournament(data.tournamentId);

      if (started) {
        const tournament = this.tournamentEngine.getTournament(data.tournamentId);
        if (tournament) {
          this.io.to(`tournament_${data.tournamentId}`).emit('tournament-started', tournament);

          // Create tournament table
          this.createTournamentTable(tournament);
        }
      } else {
        socket.emit('error', { message: 'Could not start tournament' });
      }
    } catch (error) {
      socket.emit('error', { message: (error as Error).message });
    }
  }

  private createTournamentTable(tournament: any): void {
    // Create a special room for the tournament
    const roomId = `tournament_table_${tournament.id}`;
    const blinds = this.tournamentEngine.getCurrentBlinds(tournament.id) || { small: 10, big: 20 };

    const room: Room = {
      id: roomId,
      name: `${tournament.name} - Table`,
      maxPlayers: tournament.maxPlayers,
      currentPlayers: 0,
      blinds: blinds,
      buyIn: tournament.buyIn,
      isPrivate: true, // Tournament tables are private
    };

    const gameRoom: GameRoom = {
      room,
      players: new Map(),
    };

    this.rooms.set(roomId, gameRoom);

    // Add tournament participants to the table
    tournament.participants.forEach((participant: any) => {
      if (!participant.isEliminated) {
        const player: Player = {
          id: participant.userId,
          name: participant.username,
          chips: participant.chipCount,
          cards: [],
          currentBet: 0,
          totalBet: 0,
          position: gameRoom.players.size,
          isActive: true,
          isFolded: false,
          isAllIn: false,
        };

        gameRoom.players.set(participant.userId, player);
        this.playerToRoom.set(participant.userId, roomId);
      }
    });

    // Start the tournament game
    if (gameRoom.players.size >= 2) {
      const game = new PokerGame(roomId, Array.from(gameRoom.players.values()), blinds);
      gameRoom.game = game;
      gameRoom.statsTracker = new GameStatsTracker();

      game.startGame();

      this.io.to(`tournament_${tournament.id}`).emit('tournament-table-ready', {
        roomId,
        gameState: game.getGameState()
      });
    }
  }
}

