import { Tournament, TournamentState, TournamentBlindLevel, TournamentPlayer } from '../../shared/types.js';
import { storage } from '../storage';

export class TournamentEngine {
  private tournaments: Map<string, TournamentState> = new Map();
  private blindTimers: Map<string, NodeJS.Timeout> = new Map();

  constructor() {
    this.setupDefaultBlindStructure();
  }

  private setupDefaultBlindStructure(): TournamentBlindLevel[] {
    return [
      { level: 1, smallBlind: 10, bigBlind: 20, duration: 15 },
      { level: 2, smallBlind: 15, bigBlind: 30, duration: 15 },
      { level: 3, smallBlind: 25, bigBlind: 50, duration: 15 },
      { level: 4, smallBlind: 50, bigBlind: 100, duration: 15 },
      { level: 5, smallBlind: 75, bigBlind: 150, duration: 15 },
      { level: 6, smallBlind: 100, bigBlind: 200, duration: 15 },
      { level: 7, smallBlind: 150, bigBlind: 300, duration: 15 },
      { level: 8, smallBlind: 200, bigBlind: 400, duration: 15 },
      { level: 9, smallBlind: 300, bigBlind: 600, duration: 15 },
      { level: 10, smallBlind: 500, bigBlind: 1000, duration: 15 },
    ];
  }

  private getPayoutStructure(playerCount: number): { position: number; percentage: number }[] {
    if (playerCount <= 3) {
      return [{ position: 1, percentage: 100 }];
    } else if (playerCount <= 6) {
      return [
        { position: 1, percentage: 70 },
        { position: 2, percentage: 30 },
      ];
    } else {
      return [
        { position: 1, percentage: 50 },
        { position: 2, percentage: 30 },
        { position: 3, percentage: 20 },
      ];
    }
  }

  createTournament(
    name: string,
    buyIn: number,
    maxPlayers: number,
    blindStructure?: TournamentBlindLevel[]
  ): TournamentState {
    const tournamentId = `tournament_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    const tournament: TournamentState = {
      id: tournamentId,
      name,
      buyIn,
      prizePool: 0,
      maxPlayers,
      currentPlayers: 0,
      status: 'waiting',
      blindStructure: blindStructure || this.setupDefaultBlindStructure(),
      currentBlindLevel: 0,
      createdAt: new Date(),
      participants: [],
      payoutStructure: this.getPayoutStructure(maxPlayers),
    };

    this.tournaments.set(tournamentId, tournament);
    return tournament;
  }

  joinTournament(tournamentId: string, userId: string, username: string): boolean {
    const tournament = this.tournaments.get(tournamentId);

    if (!tournament || tournament.status !== 'waiting' || tournament.currentPlayers >= tournament.maxPlayers) {
      return false;
    }

    // Check if player already joined
    const existingPlayer = tournament.participants.find(p => p.userId === userId);
    if (existingPlayer) {
      return false;
    }

    const player: TournamentPlayer = {
      userId,
      username,
      chipCount: 1500, // Starting chip count
      isEliminated: false,
      joinedAt: new Date(),
    };

    tournament.participants.push(player);
    tournament.currentPlayers++;
    tournament.prizePool += tournament.buyIn;

    return true;
  }

  startTournament(tournamentId: string): boolean {
    const tournament = this.tournaments.get(tournamentId);

    if (!tournament || tournament.status !== 'waiting' || tournament.currentPlayers < 2) {
      return false;
    }

    tournament.status = 'active';
    tournament.startedAt = new Date();

    // Start first blind level
    this.startBlindTimer(tournamentId);

    return true;
  }

  private startBlindTimer(tournamentId: string): void {
    const tournament = this.tournaments.get(tournamentId);
    if (!tournament) return;

    const currentLevel = tournament.blindStructure[tournament.currentBlindLevel];
    if (!currentLevel) return;

    // Clear existing timer
    const existingTimer = this.blindTimers.get(tournamentId);
    if (existingTimer) {
      clearTimeout(existingTimer);
    }

    // Set next blind increase
    const nextIncrease = new Date();
    nextIncrease.setMinutes(nextIncrease.getMinutes() + currentLevel.duration);
    tournament.nextBlindIncrease = nextIncrease;

    // Start new timer
    const timer = setTimeout(() => {
      this.increaseBlinds(tournamentId);
    }, currentLevel.duration * 60 * 1000); // Convert minutes to milliseconds

    this.blindTimers.set(tournamentId, timer);
  }

  private increaseBlinds(tournamentId: string): void {
    const tournament = this.tournaments.get(tournamentId);
    if (!tournament || tournament.status !== 'active') return;

    if (tournament.currentBlindLevel < tournament.blindStructure.length - 1) {
      tournament.currentBlindLevel++;
      console.log(`Tournament ${tournamentId}: Blinds increased to level ${tournament.currentBlindLevel + 1}`);

      // Update game table if it exists
      if (tournament.currentTable) {
        const newLevel = tournament.blindStructure[tournament.currentBlindLevel];
        tournament.currentTable.blinds = {
          small: newLevel.smallBlind,
          big: newLevel.bigBlind,
        };
      }

      // Start next timer
      this.startBlindTimer(tournamentId);
    }
  }

  async eliminatePlayer(tournamentId: string, userId: string): Promise<void> {
    const tournament = this.tournaments.get(tournamentId);
    if (!tournament) return;

    const player = tournament.participants.find(p => p.userId === userId);
    if (!player || player.isEliminated) return;

    player.isEliminated = true;
    player.eliminatedAt = new Date();
    player.position = tournament.participants.filter(p => !p.isEliminated).length + 1;

    console.log(`Player ${player.username} eliminated from tournament ${tournamentId} in position ${player.position}`);

    // Check if tournament is finished
    const remainingPlayers = tournament.participants.filter(p => !p.isEliminated);
    if (remainingPlayers.length <= 1) {
      await this.finishTournament(tournamentId);
    }
  }

  private async finishTournament(tournamentId: string): Promise<void> {
    const tournament = this.tournaments.get(tournamentId);
    if (!tournament) return;

    tournament.status = 'completed';
    tournament.endedAt = new Date();

    // Clear blind timer
    const timer = this.blindTimers.get(tournamentId);
    if (timer) {
      clearTimeout(timer);
      this.blindTimers.delete(tournamentId);
    }

    // Set winner
    const winner = tournament.participants.find(p => !p.isEliminated);
    if (winner) {
      tournament.winnerId = winner.userId;
      winner.position = 1;
    }

    // Calculate payouts
    await this.calculatePayouts(tournament);

    console.log(`Tournament ${tournamentId} completed. Winner: ${winner?.username}`);
  }

  private async calculatePayouts(tournament: TournamentState): Promise<void> {
    const totalPrizePool = tournament.prizePool;

    for (const payout of tournament.payoutStructure) {
      const player = tournament.participants.find(p => p.position === payout.position);
      if (player && player.userId) {
        const amount = (totalPrizePool * payout.percentage) / 100;

        try {
          // Create transaction record for tournament winnings (tracking only)
          await storage.createTransaction({
            userId: player.userId,
            type: 'win',
            amount: amount.toString(),
            description: `Tournament prize: ${tournament.name} (Position ${payout.position})`,
            stripeTransactionId: null,
            status: 'completed',
          });

          console.log(`Tournament payout: Player ${player.username} (ID: ${player.userId}) won $${amount} for position ${payout.position} (tracking only)`);
        } catch (error) {
          console.error(`Error logging tournament winnings for player ${player.username}:`, error);
        }
      } else {
        console.warn(`No payout for position ${payout.position} - player not found or no userId`);
      }
    }
  }

  getTournament(tournamentId: string): TournamentState | undefined {
    return this.tournaments.get(tournamentId);
  }

  getAllTournaments(): TournamentState[] {
    return Array.from(this.tournaments.values());
  }

  getActiveTournaments(): TournamentState[] {
    return Array.from(this.tournaments.values()).filter(t => t.status === 'waiting' || t.status === 'active');
  }

  updatePlayerChips(tournamentId: string, userId: string, newChipCount: number): void {
    const tournament = this.tournaments.get(tournamentId);
    if (!tournament) return;

    const player = tournament.participants.find(p => p.userId === userId);
    if (player) {
      player.chipCount = newChipCount;

      // Check for elimination
      if (newChipCount <= 0 && !player.isEliminated) {
        this.eliminatePlayer(tournamentId, userId);
      }
    }
  }

  getCurrentBlinds(tournamentId: string): { small: number; big: number } | null {
    const tournament = this.tournaments.get(tournamentId);
    if (!tournament) return null;

    const currentLevel = tournament.blindStructure[tournament.currentBlindLevel];
    return currentLevel ? { small: currentLevel.smallBlind, big: currentLevel.bigBlind } : null;
  }

  getTimeToNextBlind(tournamentId: string): number | null {
    const tournament = this.tournaments.get(tournamentId);
    if (!tournament || !tournament.nextBlindIncrease) return null;

    return tournament.nextBlindIncrease.getTime() - Date.now();
  }
}

