import { io, Socket } from 'socket.io-client';
import { Room, GameState, Player, Card, PokerAction } from '@shared/types';

class SocketClient {
  private socket: Socket | null = null;
  private isConnected = false;

  connect(): Promise<void> {
    return new Promise((resolve, reject) => {
      if (this.socket?.connected) {
        resolve();
        return;
      }

      // ðŸ”§ ENHANCED CLIENT STABILITY - Robuste Connection mit Retry-Logic
      this.socket = io(window.location.origin, {
        transports: ['websocket', 'polling'],
        autoConnect: true,
        reconnection: true,
        reconnectionAttempts: 10,
        reconnectionDelay: 1000,
        reconnectionDelayMax: 5000,
        timeout: 10000,
        forceNew: false
      });

      this.socket.on('connect', () => {
        console.log('Connected to server');
        this.isConnected = true;

        // Send authentication token if available
        try {
          const authData = localStorage.getItem('poker-auth');
          if (authData) {
            const parsedAuth = JSON.parse(authData);
            if (parsedAuth.state?.token) {
              console.log('Sending authentication token...');
              this.socket?.emit('authenticate', { token: parsedAuth.state.token });
            }
          }
        } catch (error) {
          console.error('Error reading auth token:', error);
        }

        resolve();
      });

      this.socket.on('disconnect', () => {
        console.log('Disconnected from server');
        this.isConnected = false;
      });

      this.socket.on('connect_error', (error) => {
        console.error('ðŸš¨ [CLIENT] Connection error:', error);
        this.isConnected = false;
        // Don't reject immediately - let reconnection logic handle it
        setTimeout(() => reject(error), 5000);
      });

      // ðŸ”§ Enhanced reconnection handling
      this.socket.on('reconnect', (attemptNumber) => {
        console.log(`âœ… [CLIENT] Reconnected after ${attemptNumber} attempts`);
        this.isConnected = true;
        // Re-authenticate on reconnect
        try {
          const authData = localStorage.getItem('poker-auth');
          if (authData) {
            const parsedAuth = JSON.parse(authData);
            if (parsedAuth.state?.token) {
              this.socket?.emit('authenticate', { token: parsedAuth.state.token });
            }
          }
        } catch (error) {
          console.error('Error re-authenticating on reconnect:', error);
        }
      });

      this.socket.on('reconnect_error', (error) => {
        console.error('ðŸš¨ [CLIENT] Reconnection failed:', error);
      });

      this.socket.on('reconnect_failed', () => {
        console.error('ðŸš¨ [CLIENT] All reconnection attempts failed');
        this.isConnected = false;
      });
    });
  }

  disconnect(): void {
    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
      this.isConnected = false;
    }
  }

  // Room management
  getRooms(): void {
    this.socket?.emit('get-rooms');
  }

  createRoom(data: {
    name: string;
    maxPlayers: number;
    blinds: { small: number; big: number };
    buyIn: number;
    isPrivate: boolean;
    password?: string;
    playerName: string;
  }): void {
    console.log('[DEBUG] Emitting create-room event with data:', data);
    this.socket?.emit('create-room', data);
  }

  createSinglePlayerRoom(data: {
    name: string;
    blinds: { small: number; big: number };
    buyIn: number;
    playerName: string;
    botCount?: number;
  }): void {
    console.log('[DEBUG] Emitting create-singleplayer-room event with data:', data);
    this.socket?.emit('create-singleplayer-room', data);
  }

  joinRoom(data: {
    roomId: string;
    playerName: string;
    buyIn: number;
    password?: string;
  }): void {
    console.log('[DEBUG] Emitting join-room event with data:', data);
    this.socket?.emit('join-room', data);
  }

  leaveRoom(): void {
    this.socket?.emit('leave-room');
  }

  // Game actions
  startGame(): void {
    this.socket?.emit('start-game');
  }

  playerAction(action: PokerAction): void {
    this.socket?.emit('player-action', action);
  }

  // Event listeners
  onRoomsList(callback: (rooms: Room[]) => void): void {
    this.socket?.on('rooms-list', callback);
  }

  onRoomCreated(callback: (data: { roomId: string; room: Room }) => void): void {
    this.socket?.on('room-created', callback);
  }

  onJoinedRoom(callback: (data: {
    roomId: string;
    room: Room;
    players: Player[];
    gameState?: GameState;
  }) => void): void {
    this.socket?.on('joined-room', callback);
  }

  onPlayerJoined(callback: (data: { player: Player; room: Room }) => void): void {
    this.socket?.on('player-joined', callback);
  }

  onPlayerLeft(callback: (data: { playerId: string; room: Room }) => void): void {
    this.socket?.on('player-left', callback);
  }

  onGameStarted(callback: (gameState: GameState) => void): void {
    this.socket?.on('game-started', callback);
  }

  onGameUpdated(callback: (gameState: GameState) => void): void {
    this.socket?.on('game-updated', callback);
  }

  onPlayerAction(callback: (data: { action: any; gameState: GameState }) => void): void {
    this.socket?.on('player-action', callback);
  }

  onPrivateCards(callback: (cards: Card[]) => void): void {
    this.socket?.on('private-cards', callback);
  }

  onError(callback: (error: { message: string }) => void): void {
    this.socket?.on('error', callback);
  }

  // Generic event listener for any event (for custom events like ironic-event)
  on(event: string, callback: (...args: any[]) => void): void {
    this.socket?.on(event, callback);
  }

  // Remove event listeners
  off(event: string, callback?: (...args: any[]) => void): void {
    if (callback) {
      this.socket?.off(event, callback);
    } else {
      this.socket?.off(event);
    }
  }

  getConnectionStatus(): boolean {
    return this.isConnected;
  }

  getSocketId(): string | null {
    return this.socket?.id || null;
  }
}

export const socketClient = new SocketClient();

