import React from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { useAuth, type AuthUser } from '@/lib/stores/useAuth';

interface UserInfoProps {
  user: AuthUser;
  showLogout?: boolean;
  compact?: boolean;
}

const UserInfo: React.FC<UserInfoProps> = ({ user, showLogout = true, compact = false }) => {
  const { logout } = useAuth();

  const winRate = user.handsPlayed > 0 ? ((user.handsWon / user.handsPlayed) * 100).toFixed(1) : '0.0';
  const gameWinRate = user.gamesPlayed > 0 ? ((user.gamesWon / user.gamesPlayed) * 100).toFixed(1) : '0.0';

  if (compact) {
    return (
      <div className="bg-gray-800 rounded-lg p-3 text-white">
        <div className="flex items-center justify-between">
          <div>
            <div className="font-semibold">{user.username}</div>
            <div className="text-sm text-green-400">${parseFloat(user.balance).toLocaleString()}</div>
          </div>
          {showLogout && (
            <Button onClick={logout} variant="outline" size="sm">
              Logout
            </Button>
          )}
        </div>
      </div>
    );
  }

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
        <CardTitle className="text-lg">{user.username}</CardTitle>
        {showLogout && (
          <Button onClick={logout} variant="outline" size="sm">
            Logout
          </Button>
        )}
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Balance */}
        <div className="text-center">
          <div className="text-2xl font-bold text-green-600">
            ${parseFloat(user.balance).toLocaleString()}
          </div>
          <div className="text-sm text-gray-600">Current Balance</div>
        </div>

        {/* Statistics Grid */}
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div className="text-center p-3 bg-gray-50 rounded-lg">
            <div className="font-semibold text-lg">{user.handsPlayed}</div>
            <div className="text-gray-600">Hands Played</div>
          </div>
          <div className="text-center p-3 bg-gray-50 rounded-lg">
            <div className="font-semibold text-lg">{winRate}%</div>
            <div className="text-gray-600">Hand Win Rate</div>
          </div>
          <div className="text-center p-3 bg-gray-50 rounded-lg">
            <div className="font-semibold text-lg">{user.gamesPlayed}</div>
            <div className="text-gray-600">Games Played</div>
          </div>
          <div className="text-center p-3 bg-gray-50 rounded-lg">
            <div className="font-semibold text-lg">{gameWinRate}%</div>
            <div className="text-gray-600">Game Win Rate</div>
          </div>
        </div>

        {/* Earnings */}
        <div className="space-y-2">
          <div className="flex justify-between">
            <span className="text-gray-600">Total Winnings:</span>
            <span className="font-semibold text-green-600">
              +${parseFloat(user.totalWinnings).toLocaleString()}
            </span>
          </div>
          <div className="flex justify-between">
            <span className="text-gray-600">Total Losses:</span>
            <span className="font-semibold text-red-600">
              -${parseFloat(user.totalLosses).toLocaleString()}
            </span>
          </div>
          <div className="flex justify-between border-t pt-2">
            <span className="text-gray-600">Net Profit:</span>
            <span className={`font-semibold ${
              parseFloat(user.totalWinnings) - parseFloat(user.totalLosses) >= 0 
                ? 'text-green-600' 
                : 'text-red-600'
            }`}>
              {parseFloat(user.totalWinnings) - parseFloat(user.totalLosses) >= 0 ? '+' : ''}
              ${(parseFloat(user.totalWinnings) - parseFloat(user.totalLosses)).toLocaleString()}
            </span>
          </div>
        </div>

        {/* Badges */}
        <div className="flex gap-2 flex-wrap">
          {user.handsWon > 100 && (
            <Badge variant="secondary">Hand Winner</Badge>
          )}
          {user.gamesWon > 10 && (
            <Badge variant="secondary">Game Champion</Badge>
          )}
          {parseFloat(user.totalWinnings) > 10000 && (
            <Badge variant="outline">High Roller</Badge>
          )}
        </div>

        {/* Member since */}
        <div className="text-xs text-gray-500 text-center">
          Member since {new Date(user.createdAt).toLocaleDateString()}
        </div>
      </CardContent>
    </Card>
  );
};

export default UserInfo;

