import React from 'react';
import { Card as CardType } from '@shared/types';

interface CardProps {
  card?: CardType;
  isHidden?: boolean;
  size?: 'small' | 'medium' | 'large';
  className?: string;
}

const Card: React.FC<CardProps> = ({ 
  card, 
  isHidden = false, 
  size = 'medium',
  className = '' 
}) => {
  const getSuitSymbol = (suit: string) => {
    switch (suit) {
      case 'hearts': return 'â™¥';
      case 'diamonds': return 'â™¦';
      case 'clubs': return 'â™£';
      case 'spades': return 'â™ ';
      default: return '';
    }
  };

  const getSuitColor = (suit: string) => {
    return ['hearts', 'diamonds'].includes(suit) ? 'text-red-500' : 'text-black';
  };

  const getSizeClasses = () => {
    switch (size) {
      case 'small': return 'w-12 h-16 text-xs';
      case 'medium': return 'w-16 h-24 text-sm';
      case 'large': return 'w-20 h-28 text-base';
      default: return 'w-16 h-24 text-sm';
    }
  };

  if (isHidden || !card) {
    return (
      <div className={`
        ${getSizeClasses()}
        bg-gradient-to-br from-red-800 via-red-700 to-red-900 border-2 border-red-900 rounded-lg
        flex items-center justify-center
        shadow-lg relative overflow-hidden
        ${className}
      `}>
        {/* ðŸŽ² Classic Card Back Pattern */}
        <div className="absolute inset-0 opacity-20">
          <div className="w-full h-full bg-gradient-to-br from-red-600 to-red-800"></div>
          <div className="absolute inset-1 border border-red-500 rounded"></div>
        </div>
        <div className="relative text-red-300 text-lg font-bold">B</div>
      </div>
    );
  }

  return (
    <div className={`
      ${getSizeClasses()}
      bg-white border-2 border-gray-400 rounded-lg
      flex flex-col items-center justify-between
      shadow-xl p-1 relative
      ${className}
    `}>
      {/* Classic card shadow */}
      <div className="absolute inset-0 bg-gray-100 rounded-lg -z-10 transform translate-x-0.5 translate-y-0.5"></div>
      <div className={`${getSuitColor(card.suit)} font-bold flex flex-col items-center`}>
        <span>{card.rank}</span>
        <span className="text-lg leading-none">{getSuitSymbol(card.suit)}</span>
      </div>

      <div className={`${getSuitColor(card.suit)} text-lg leading-none transform rotate-180`}>
        {getSuitSymbol(card.suit)}
      </div>
    </div>
  );
};

export default Card;

client/src/components/poker/ChipStack.ts.content:
import React from 'react';

interface ChipStackProps {
  amount: number;
  size?: 'small' | 'medium' | 'large';
  showAmount?: boolean;
  className?: string;
}

const ChipStack: React.FC<ChipStackProps> = ({ 
  amount, 
  size = 'medium', 
  showAmount = true,
  className = '' 
}) => {
  const getChipColor = (value: number) => {
    if (value >= 1000) return 'bg-purple-500 border-purple-600';
    if (value >= 500) return 'bg-pink-500 border-pink-600';
    if (value >= 100) return 'bg-black border-gray-700';
    if (value >= 25) return 'bg-green-500 border-green-600';
    if (value >= 5) return 'bg-red-500 border-red-600';
    return 'bg-white border-gray-300';
  };

  const getSizeClasses = () => {
    switch (size) {
      case 'small': return 'w-6 h-6 text-xs';
      case 'medium': return 'w-8 h-8 text-sm';
      case 'large': return 'w-10 h-10 text-base';
      default: return 'w-8 h-8 text-sm';
    }
  };

  const getStackHeight = () => {
    if (amount <= 0) return 0;
    const baseChips = Math.min(Math.floor(amount / 5), 10); // Max 10 visible chips
    return Math.max(1, baseChips);
  };

  const stackHeight = getStackHeight();

  if (amount <= 0) {
    return null;
  }

  return (
    <div className={`relative inline-block ${className}`}>
      {/* Chip stack */}
      <div className="relative">
        {Array.from({ length: stackHeight }, (_, i) => (
          <div
            key={i}
            className={`
              ${getSizeClasses()}
              ${getChipColor(amount)}
              rounded-full border-2
              absolute
              shadow-sm
            `}
            style={{
              bottom: `${i * 2}px`,
              zIndex: stackHeight - i
            }}
          />
        ))}
      </div>

      {/* Amount label */}
      {showAmount && (
        <div className="absolute -bottom-6 left-1/2 transform -translate-x-1/2 whitespace-nowrap">
          <span className="text-xs font-bold text-white bg-black bg-opacity-75 px-2 py-1 rounded">
            {amount.toLocaleString()}
          </span>
        </div>
      )}
    </div>
  );
};

export default ChipStack;

client/src/components/poker/GameUI.tsx.content:
import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Slider } from '@/components/ui/slider';
import { usePoker } from '@/lib/stores/usePoker';
import { useLanguage } from '@/lib/stores/useLanguage';
import { socketClient } from '@/lib/socketClient';
import { IronicEventModal } from './IronicEventModal';

const GameUI: React.FC = () => {
  const {
    gameState,
    selectedAction,
    raiseAmount,
    setSelectedAction,
    setRaiseAmount,
    isMyTurn,
    canCheck,
    canCall,
    canRaise,
    getCallAmount,
    getMinRaise,
    getMaxRaise,
    getMyPlayer
  } = usePoker();

  const { t } = useLanguage();

  const [customRaise, setCustomRaise] = useState(0);
  const [currentIronicEvent, setCurrentIronicEvent] = useState<any>(null);
  const [rouletteNumber, setRouletteNumber] = useState<number | undefined>();
  const myPlayer = getMyPlayer();

  // ðŸŽ° Listen for Bojek Poker's amazing ironic events! ðŸŽ²
  useEffect(() => {
    const handleIronicEvent = (eventData: any) => {
      console.log('[FRONTEND] Received ironic event:', eventData);
      setCurrentIronicEvent(eventData.event);
      setRouletteNumber(eventData.rouletteNumber);
    };

    // Check if socketClient has the on method (connected state)
    if (socketClient && typeof socketClient.on === 'function') {
      socketClient.on('ironic-event', handleIronicEvent);

      return () => {
        if (socketClient && typeof socketClient.off === 'function') {
          socketClient.off('ironic-event', handleIronicEvent);
        }
      };
    } else {
      console.warn('[BOJEK POKER] SocketClient not ready for ironic events');
    }
  }, []);

  if (!gameState || !myPlayer || gameState.phase === 'waiting') {
    return (
      <>
        {/* ðŸŽ° Always show Bojek Poker Ironic Events even when waiting! ðŸŽ² */}
        <IronicEventModal
          event={currentIronicEvent}
          rouletteNumber={rouletteNumber}
          onClose={() => {
            setCurrentIronicEvent(null);
            setRouletteNumber(undefined);
          }}
        />
      </>
    );
  }

  const handleAction = (actionType: 'fold' | 'call' | 'raise' | 'check' | 'bet' | 'all-in') => {
    if (!isMyTurn()) return;

    let amount = undefined;
    if (actionType === 'raise' || actionType === 'bet') {
      amount = customRaise || raiseAmount;
    }

    socketClient.playerAction({
      type: actionType,
      amount,
      playerId: myPlayer.id
    });

    setSelectedAction(null);
    setCustomRaise(0);
  };

  const quickRaiseAmounts = [
    { label: '2x BB', amount: gameState.blinds.big * 2 },
    { label: '3x BB', amount: gameState.blinds.big * 3 },
    { label: '5x BB', amount: gameState.blinds.big * 5 },
    { label: 'Pot', amount: gameState.pot },
  ];

  if (!isMyTurn()) {
    return (
      <>
        {/* ðŸŽ° Bojek Poker Ironic Event Modal for ALL players! ðŸŽ² */}
        <IronicEventModal
          event={currentIronicEvent}
          rouletteNumber={rouletteNumber}
          onClose={() => {
            setCurrentIronicEvent(null);
            setRouletteNumber(undefined);
          }}
        />

        <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 bg-gradient-to-r from-card via-muted to-card/80 backdrop-blur-lg rounded-xl p-6 shadow-2xl border border-border">
          <div className="text-center">
            <div className="text-muted-foreground text-sm mb-2">{t('waitingForTurn')}</div>
            <div className="text-foreground text-xl font-bold bg-gradient-to-r from-primary via-accent to-secondary bg-clip-text text-transparent">
              {t('playerTurn', { playerName: gameState.players[gameState.currentPlayerIndex]?.name || '' })}
            </div>
          </div>
        </div>
      </>
    );
  }

  return (
    <>
      {/* ðŸŽ° Bojek Poker Ironic Event Modal ðŸŽ² */}
      <IronicEventModal
        event={currentIronicEvent}
        rouletteNumber={rouletteNumber}
        onClose={() => {
          setCurrentIronicEvent(null);
          setRouletteNumber(undefined);
        }}
      />

      <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 bg-gradient-to-r from-card via-muted to-card/90 backdrop-blur-xl rounded-2xl p-8 shadow-2xl border-2 border-primary/30">
      <div className="flex flex-col gap-4">
        {/* Action buttons */}
        <div className="flex gap-3">
          {/* Fold */}
          <Button 
            onClick={() => handleAction('fold')}
            variant="destructive"
            size="lg"
            className=""
          >
            ðŸš« {t('fold')}
          </Button>

          {/* Check/Call */}
          {canCheck() ? (
            <Button 
              onClick={() => handleAction('check')}
              variant="secondary"
              size="lg"
              className=""
            >
              âœ… {t('check')}
            </Button>
          ) : canCall() ? (
            <Button 
              onClick={() => handleAction('call')}
              variant="gaming"
              size="lg"
              className=""
            >
              ðŸ’° {t('call')} ${getCallAmount().toLocaleString()}
            </Button>
          ) : null}

          {/* Raise/Bet */}
          {canRaise() && (
            <Button 
              onClick={() => setSelectedAction(selectedAction === 'raise' ? null : 'raise')}
              variant={selectedAction === 'raise' ? 'neon' : 'outline'}
              size="lg"
              className=""
            >
              ðŸš€ {gameState.currentBet > 0 ? 'Raise' : 'Bet'}
            </Button>
          )}

          {/* All-in */}
          {myPlayer.chips > 0 && (
            <Button 
              onClick={() => handleAction('all-in')}
              variant="outline"
              size="lg"
              className="border-2 border-yellow-400 bg-yellow-400 text-black hover:bg-yellow-300 hover:border-yellow-300 font-bold"
            >
              All-in ${myPlayer.chips.toLocaleString()}
            </Button>
          )}
        </div>

        {/* Raise controls */}
        {selectedAction === 'raise' && canRaise() && (
          <div className="bg-gray-800 rounded-lg p-4 space-y-4">
            <div className="flex justify-between text-white text-sm">
              <span>Min: ${getMinRaise().toLocaleString()}</span>
              <span>Max: ${getMaxRaise().toLocaleString()}</span>
            </div>

            {/* Slider */}
            <div className="space-y-2">
              <Slider
                value={[customRaise || getMinRaise()]}
                onValueChange={([value]) => setCustomRaise(value)}
                min={getMinRaise()}
                max={getMaxRaise()}
                step={gameState.blinds.small}
                className="w-full"
              />
              <div className="text-center text-white font-bold">
                ${(customRaise || getMinRaise()).toLocaleString()}
              </div>
            </div>

            {/* Quick bet buttons */}
            <div className="flex gap-2 flex-wrap">
              {quickRaiseAmounts.map(({ label, amount }) => (
                <Button
                  key={label}
                  onClick={() => setCustomRaise(Math.min(amount, getMaxRaise()))}
                  variant="outline"
                  size="sm"
                  disabled={amount > getMaxRaise()}
                  className={amount > getMaxRaise() 
                    ? "bg-gray-600 border-gray-500 text-gray-400 cursor-not-allowed" 
                    : "border-2 border-white bg-gray-700 text-white hover:bg-white hover:text-black font-bold"
                  }
                >
                  {label}
                </Button>
              ))}
            </div>

            {/* Confirm raise */}
            <div className="flex gap-2">
              <Button
                onClick={() => handleAction(gameState.currentBet > 0 ? 'raise' : 'bet')}
                variant="default"
                className="flex-1 bg-purple-600 hover:bg-purple-700 text-white font-bold"
                disabled={customRaise < getMinRaise()}
              >
                {gameState.currentBet > 0 ? 'Raise' : 'Bet'} ${(customRaise || getMinRaise()).toLocaleString()}
              </Button>
              <Button
                onClick={() => setSelectedAction(null)}
                variant="outline"
                className="border-2 border-gray-400 bg-gray-400 text-black hover:bg-gray-300 font-bold"
              >
                Cancel
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
    </>
  );
};

export default GameUI;

