import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import { usePoker } from '@/lib/stores/usePoker';
import { useAuth } from '@/lib/stores/useAuth';
import { useLanguage } from '@/lib/stores/useLanguage';
import { socketClient } from '@/lib/socketClient';
import { Room } from '@shared/types';
import AuthModal from '@/components/auth/AuthModal';
import UserInfo from '@/components/auth/UserInfo';
import { TournamentLobby } from './TournamentLobby';

const Lobby: React.FC = () => {
  const { 
    rooms, 
    isConnected, 
    setShowLobby,
    setCurrentRoomId,
    setMyPlayerId 
  } = usePoker();

  const { user, isAuthenticated, fetchProfile } = useAuth();
  const { t } = useLanguage();

  const [playerName, setPlayerName] = useState('');
  const [roomName, setRoomName] = useState('');
  const [maxPlayers, setMaxPlayers] = useState(6);
  const [smallBlind, setSmallBlind] = useState(5);
  const [bigBlind, setBigBlind] = useState(10);
  const [buyIn, setBuyIn] = useState(1000);
  const [isPrivate, setIsPrivate] = useState(false);
  const [password, setPassword] = useState('');
  const [joinPassword, setJoinPassword] = useState('');
  const [selectedRoom, setSelectedRoom] = useState<string>('');
  const [loading, setLoading] = useState(false);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const [showTournamentLobby, setShowTournamentLobby] = useState(false);

  useEffect(() => {
    if (isConnected) {
      socketClient.getRooms();
    }
  }, [isConnected]);

  useEffect(() => {
    // Set player name from authenticated user
    if (user) {
      setPlayerName(user.username);
    }
  }, [user]);

  useEffect(() => {
    // Fetch profile if token exists but user is not loaded
    if (isAuthenticated && !user) {
      fetchProfile();
    }
  }, [isAuthenticated, user, fetchProfile]);

  useEffect(() => {
    // Only set up listeners when socket is connected
    if (!isConnected) {
      console.log('[DEBUG] Socket not connected, skipping event listener setup');
      return;
    }

    console.log('[DEBUG] Setting up socket event listeners');

    // Set up socket event listeners
    const handleRoomCreated = (data: { roomId: string; room: Room }) => {
      console.log('[DEBUG] Room created event received:', data);
      setLoading(false);
      setCurrentRoomId(data.roomId);
      setMyPlayerId(socketClient.getSocketId());
      setShowLobby(false);
    };

    const handleJoinedRoom = (data: {
      roomId: string;
      room: Room;
      players: any[];
      gameState?: any;
    }) => {
      console.log('[DEBUG] Joined room event received:', data);
      setLoading(false);
      setCurrentRoomId(data.roomId);
      setMyPlayerId(socketClient.getSocketId());
      setShowLobby(false);
    };

    const handleError = (error: { message: string }) => {
      console.log('[DEBUG] Error event received:', error);
      setLoading(false);
      alert(error.message);
    };

    socketClient.onRoomCreated(handleRoomCreated);
    socketClient.onJoinedRoom(handleJoinedRoom);
    socketClient.onError(handleError);

    console.log('[DEBUG] Event listeners set up successfully');

    return () => {
      console.log('[DEBUG] Cleaning up event listeners');
      socketClient.off('room-created', handleRoomCreated);
      socketClient.off('joined-room', handleJoinedRoom);
      socketClient.off('error', handleError);
    };
  }, [isConnected, setCurrentRoomId, setMyPlayerId, setShowLobby]);

  const handleCreateRoom = async () => {
    if (!playerName.trim() || !roomName.trim()) {
      alert(t('playerNameAndRoomNameRequired'));
      return;
    }

    setLoading(true);
    socketClient.createRoom({
      name: roomName,
      maxPlayers,
      blinds: { small: smallBlind, big: bigBlind },
      buyIn,
      isPrivate,
      password: isPrivate ? password : undefined,
      playerName: playerName.trim()
    });
  };

  const handleCreateSinglePlayerRoom = async () => {
    if (!playerName.trim()) {
      alert(t('playerNameRequired'));
      return;
    }

    setLoading(true);
    socketClient.createSinglePlayerRoom({
      name: t('singlePlayerRoomName', { playerName }),
      blinds: { small: smallBlind, big: bigBlind },
      buyIn,
      playerName: playerName.trim(),
      botCount: 3 // 3 bots + 1 human = 4 players total
    });
  };

  const handleJoinRoom = async (roomId: string) => {
    if (!playerName.trim()) {
      alert(t('playerNameRequired'));
      return;
    }

    const room = rooms.find(r => r.id === roomId);
    if (!room) return;

    setLoading(true);
    socketClient.joinRoom({
      roomId,
      playerName: playerName.trim(),
      buyIn: room.buyIn,
      password: room.isPrivate ? joinPassword : undefined
    });
  };

  const refreshRooms = () => {
    socketClient.getRooms();
  };

  // Show auth modal if not authenticated
  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-green-900 flex items-center justify-center">
        <Card className="w-96">
          <CardContent className="pt-6">
            <div className="text-center mb-4">
              <h1 className="text-2xl font-bold text-white mb-2">{t('appName')}</h1>
              <p className="text-green-200">{t('appSubtitle')}</p>
            </div>
            <Button 
              onClick={() => setShowAuthModal(true)}
              className="w-full"
              size="lg"
            >
              {t('login')} / {t('register')}
            </Button>
          </CardContent>
        </Card>
        <AuthModal 
          isOpen={showAuthModal} 
          onClose={() => setShowAuthModal(false)} 
        />
      </div>
    );
  }

  if (!isConnected) {
    return (
      <div className="min-h-screen bg-green-900 flex items-center justify-center">
        <Card className="w-96">
          <CardContent className="pt-6">
            <div className="text-center">
              <div className="text-xl font-bold mb-2">{t('connectingToServer')}</div>
              <div className="animate-spin w-8 h-8 border-4 border-blue-500 border-t-transparent rounded-full mx-auto"></div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (showTournamentLobby) {
    return (
      <TournamentLobby 
        playerName={playerName} 
        onJoinTable={(roomId) => {
          setCurrentRoomId(roomId);
          setMyPlayerId(socketClient.getSocketId());
          setShowLobby(false);
          setShowTournamentLobby(false);
        }}
        onBackToLobby={() => setShowTournamentLobby(false)}
      />
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-800 to-green-900 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-start mb-8">
          <div>
            <h1 className="text-4xl font-bold text-white mb-2">{t('appName')}</h1>
            <p className="text-green-200">{t('createRoomSubtitle')}</p>
          </div>
          {user && (
            <div className="w-80">
              <UserInfo user={user} compact={true} />
            </div>
          )}
        </div>

        <Tabs defaultValue="join" className="w-full">
          <TabsList className="grid w-full grid-cols-4 mb-6">
            <TabsTrigger value="join">{t('joinRoom')}</TabsTrigger>
            <TabsTrigger value="create">{t('createRoom')}</TabsTrigger>
            <TabsTrigger value="singleplayer">{t('againstComputer')}</TabsTrigger>
            <TabsTrigger value="tournaments">{t('tournaments')}</TabsTrigger>
          </TabsList>

          {/* Player Name Input */}
          <div className="mb-6">
            <Card>
              <CardContent className="pt-4">
                <div className="flex gap-4 items-end">
                  <div className="flex-1">
                    <Label htmlFor="playerName">{t('playerName')}</Label>
                    <Input
                      id="playerName"
                      placeholder={t('playerName')}
                      value={playerName}
                      onChange={(e) => setPlayerName(e.target.value)}
                      maxLength={20}
                    />
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          <TabsContent value="join">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between">
                <CardTitle>{t('availableRooms')}</CardTitle>
                <Button onClick={refreshRooms} variant="outline" size="sm">
                  {t('refresh')}
                </Button>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {rooms.length === 0 ? (
                    <div className="text-center py-8 text-gray-500">
                      {t('noRoomsAvailable')}
                    </div>
                  ) : (
                    rooms.map((room) => (
                      <div
                        key={room.id}
                        className="border rounded-lg p-4 hover:bg-gray-50 transition-colors"
                      >
                        <div className="flex justify-between items-start mb-2">
                          <div>
                            <h3 className="font-semibold text-lg">{room.name}</h3>
                            <div className="flex gap-2 items-center text-sm text-gray-600">
                              <span>{t('players')}: {room.currentPlayers}/{room.maxPlayers}</span>
                              <span>â€¢</span>
                              <span>{t('buyIn').replace(' ($)', '')}: ${room.buyIn.toLocaleString()}</span>
                              <span>â€¢</span>
                              <span>{t('blinds')}: ${room.blinds.small}/${room.blinds.big}</span>
                              {room.isPrivate && <Badge variant="secondary">{t('privateRoom')}</Badge>}
                            </div>
                          </div>
                          <div className="flex gap-2">
                            {room.isPrivate && (
                              <Input
                                type="password"
                                placeholder={t('password')}
                                value={selectedRoom === room.id ? joinPassword : ''}
                                onChange={(e) => {
                                  setSelectedRoom(room.id);
                                  setJoinPassword(e.target.value);
                                }}
                                className="w-32"
                              />
                            )}
                            <Button
                              onClick={() => handleJoinRoom(room.id)}
                              disabled={loading || room.currentPlayers >= room.maxPlayers}
                              size="sm"
                            >
                              {room.currentPlayers >= room.maxPlayers ? t('full') : t('join')}
                            </Button>
                          </div>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="create">
            <Card>
              <CardHeader>
                <CardTitle>{t('createRoom')}</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="roomName">{t('roomName')}</Label>
                    <Input
                      id="roomName"
                      placeholder={t('roomName')}
                      value={roomName}
                      onChange={(e) => setRoomName(e.target.value)}
                      maxLength={30}
                    />
                  </div>
                  <div>
                    <Label htmlFor="maxPlayers">{t('maxPlayers')}</Label>
                    <Input
                      id="maxPlayers"
                      type="number"
                      min={3}
                      max={6}
                      value={maxPlayers}
                      onChange={(e) => setMaxPlayers(Math.max(3, Math.min(6, parseInt(e.target.value) || 3)))}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="smallBlind">{t('smallBlind')}</Label>
                    <Input
                      id="smallBlind"
                      type="number"
                      min={1}
                      value={smallBlind}
                      onChange={(e) => {
                        const value = parseInt(e.target.value) || 1;
                        setSmallBlind(value);
                        setBigBlind(value * 2);
                      }}
                    />
                  </div>
                  <div>
                    <Label htmlFor="bigBlind">{t('bigBlind')}</Label>
                    <Input
                      id="bigBlind"
                      type="number"
                      min={2}
                      value={bigBlind}
                      onChange={(e) => setBigBlind(parseInt(e.target.value) || 2)}
                    />
                  </div>
                  <div>
                    <Label htmlFor="buyIn">{t('buyIn')}</Label>
                    <Input
                      id="buyIn"
                      type="number"
                      min={100}
                      value={buyIn}
                      onChange={(e) => setBuyIn(parseInt(e.target.value) || 100)}
                    />
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    id="private"
                    checked={isPrivate}
                    onCheckedChange={setIsPrivate}
                  />
                  <Label htmlFor="private">{t('privateRoom')}</Label>
                </div>

                {isPrivate && (
                  <div>
                    <Label htmlFor="password">{t('roomPassword')}</Label>
                    <Input
                      id="password"
                      type="password"
                      placeholder={t('roomPassword')}
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                  </div>
                )}

                <Button
                  onClick={handleCreateRoom}
                  disabled={loading || !playerName.trim() || !roomName.trim()}
                  className="w-full"
                  size="lg"
                >
                  {loading ? t('creating') : t('createRoom')}
                </Button>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="singleplayer">
            <Card>
              <CardHeader>
                <CardTitle>{t('againstComputer')}</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-6">
                  <div className="text-center py-4">
                    <p className="text-gray-600 mb-4">
                      {t('againstComputerDesc')}
                    </p>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="spBuyIn">{t('buyIn')}</Label>
                      <Input
                        id="spBuyIn"
                        type="number"
                        placeholder="10000"
                        value={buyIn}
                        onChange={(e) => setBuyIn(Number(e.target.value))}
                        min={1000}
                        max={1000000}
                      />
                    </div>
                    <div>
                      <Label htmlFor="spSmallBlind">{t('smallBlind')}</Label>
                      <Input
                        id="spSmallBlind"
                        type="number"
                        placeholder="25"
                        value={smallBlind}
                        onChange={(e) => setSmallBlind(Number(e.target.value))}
                        min={1}
                        max={1000}
                      />
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="spBigBlind">{t('bigBlind')}</Label>
                    <Input
                      id="spBigBlind"
                      type="number"
                      placeholder="50"
                      value={bigBlind}
                      onChange={(e) => setBigBlind(Number(e.target.value))}
                      min={smallBlind * 2}
                      max={10000}
                    />
                  </div>

                  <Button
                    onClick={handleCreateSinglePlayerRoom}
                    disabled={loading || !playerName.trim()}
                    className="w-full bg-blue-600 hover:bg-blue-700"
                    size="lg"
                  >
                    {loading ? t('createGame') : t('againstComputer')}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="tournaments">
            <Card>
              <CardHeader>
                <CardTitle>{t('tournamentMode')}</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="text-center py-8">
                  <p className="text-gray-600 mb-4">
                    {t('tournamentDescription')}
                  </p>
                  <Button
                    onClick={() => setShowTournamentLobby(true)}
                    size="lg"
                    className="bg-yellow-600 hover:bg-yellow-700 text-white"
                  >
                    {t('enterTournamentLobby')}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
};

export default Lobby;

