import React from 'react';
import { Player } from '@shared/types';
import Card from './Card';
import ChipStack from './ChipStack';

interface PlayerPositionProps {
  player: Player;
  isCurrentPlayer: boolean;
  isMyself: boolean;
  showCards: boolean;
  myCards?: import('@shared/types').Card[];
  position: number;
  totalPositions: number;
}

const PlayerPosition: React.FC<PlayerPositionProps> = ({
  player,
  isCurrentPlayer,
  isMyself,
  showCards,
  myCards,
  position,
  totalPositions
}) => {
  const getPositionClasses = () => {
    // ðŸƒ Cards positioned OUTSIDE the green felt table (800x500px)
    const getOptimalLayout = (total: number) => {
      // Table is 800x500px, so positions need to be beyond these boundaries
      if (total <= 2) return { radiusX: 480, radiusY: 320, startAngle: 180 }; // Far outside for 2 players
      if (total <= 4) return { radiusX: 450, radiusY: 300, startAngle: 135 }; // Outside corners
      if (total <= 6) return { radiusX: 430, radiusY: 280, startAngle: 90 };  // Around table edges
      return { radiusX: 420, radiusY: 270, startAngle: 60 }; // Tight but outside for 7-8 players
    };

    const { radiusX, radiusY, startAngle } = getOptimalLayout(totalPositions);

    // Calculate angle with dynamic spacing
    const angleStep = 360 / totalPositions;
    const angle = (position * angleStep + startAngle) * Math.PI / 180;

    // Oval positioning (ellipse)
    const x = Math.cos(angle) * radiusX;
    const y = Math.sin(angle) * radiusY;

    return {
      transform: `translate(${x}px, ${y}px)`,
      position: 'absolute' as const,
      top: '50%',
      left: '50%',
      marginTop: '-50px',
      marginLeft: '-50px'
    };
  };

  const getPlayerStatus = () => {
    if (player.isFolded) return 'Folded';
    if (player.isAllIn) return 'All-in';
    if (!player.isActive) return 'Inactive';
    if (isCurrentPlayer) return 'Turn';
    return player.lastAction || 'Waiting';
  };

  const getStatusColor = () => {
    if (player.isFolded) return 'text-red-500';
    if (player.isAllIn) return 'text-yellow-500';
    if (!player.isActive) return 'text-gray-500';
    if (isCurrentPlayer) return 'text-green-500';
    return 'text-blue-500';
  };

  const cardsToShow = isMyself && myCards ? myCards : player.cards;

  return (
    <div style={getPositionClasses()} className="flex flex-col items-center">
      {/* Player info */}
      <div className={`
        bg-black bg-opacity-80 rounded-lg p-2 min-w-28 backdrop-blur-sm
        border-2 shadow-lg
        ${isCurrentPlayer ? 'border-amber-400 bg-amber-900 bg-opacity-40' : 'border-amber-600 border-opacity-60'}
        ${isMyself ? 'bg-green-900 bg-opacity-60 border-green-400' : ''}
      `}>
        {/* Player name and status */}
        <div className="text-center mb-1">
          <div className="text-white font-semibold text-xs truncate">
            {player.name}
            {isMyself && <span className="text-green-400 ml-1">(You)</span>}
          </div>
          <div className={`text-xs ${getStatusColor()}`}>
            {getPlayerStatus()}
          </div>
        </div>

        {/* Chips */}
        <div className="text-center mb-1">
          <div className="text-amber-200 text-xs font-bold">
            ${player.chips.toLocaleString()}
          </div>
          {player.currentBet > 0 && (
            <div className="text-amber-400 text-xs">
              Bet: ${player.currentBet.toLocaleString()}
            </div>
          )}
        </div>

        {/* Cards */}
        <div className="flex justify-center gap-1">
          {showCards && cardsToShow.length > 0 ? (
            cardsToShow.map((card, index) => (
              <Card 
                key={card?.id || index} 
                card={card} 
                size="small"
              />
            ))
          ) : (
            // Show hidden cards or card count
            Array.from({ length: player.cards.length || 2 }, (_, index) => (
              <Card 
                key={index} 
                isHidden={true} 
                size="small"
              />
            ))
          )}
        </div>
      </div>

      {/* Current bet chips */}
      {player.currentBet > 0 && (
        <div className="mt-2">
          <ChipStack 
            amount={player.currentBet} 
            size="small" 
            showAmount={true}
          />
        </div>
      )}
    </div>
  );
};

export default PlayerPosition;

