const PokerTable: React.FC = () => {
  const { gameState, myCards, myPlayerId, setCurrentRoom, setGameState, setMyCards, setShowLobby } = usePoker();
  const [showWinners, setShowWinners] = useState(false);

  // ðŸ† Auto-hide winners after 5 seconds to continue game
  useEffect(() => {
    if (gameState?.winners) {
      setShowWinners(true);
      const timer = setTimeout(() => {
        setShowWinners(false);
      }, 5000); // Auto-continue after 5 seconds

      return () => clearTimeout(timer);
    }
  }, [gameState?.winners]);

  if (!gameState) {
    return (
      <div className="w-full h-full flex items-center justify-center bg-gradient-to-br from-background via-muted to-secondary/20">
        <div className="text-center">
          <div className="text-4xl font-bold bg-gradient-to-r from-primary via-accent to-secondary bg-clip-text text-transparent mb-4">
            ðŸŽ° BOJEK POKER ðŸŽ²
          </div>
          <div className="text-foreground text-xl">
            Waiting for the most ironic game to start...
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="relative w-full h-full bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 overflow-hidden">
      {/* ðŸŸ¢ 2D Classic Table */}
      <div className="absolute inset-0">
        <ClassicTable2D />
      </div>

      {/* ðŸƒ Game UI Overlay */}
      <div className="absolute inset-0 pointer-events-none">
        {/* ðŸŸ¢ INTEGRATED POT AREA - Inside Table Surface */}
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-10">
          <div className="flex flex-col items-center">
            {/* ðŸŸ¢ POT NAHTLOS IN FILZ INTEGRIERT */}
            <div className="relative">
              {/* Pot Info direkt auf Filz ohne Box */}
              <div className="text-center mb-3">
                <div className="text-amber-300 text-xl font-bold drop-shadow-lg" style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.8)' }}>
                  ${gameState.pot.toLocaleString()}
                </div>
                <div className="text-amber-200 text-xs opacity-90 drop-shadow" style={{ textShadow: '1px 1px 2px rgba(0,0,0,0.8)' }}>
                  {gameState.phase} â€¢ Round {gameState.round}
                </div>
                {gameState.currentBet > 0 && (
                  <div className="text-amber-300 text-xs mt-1 opacity-90 drop-shadow" style={{ textShadow: '1px 1px 2px rgba(0,0,0,0.8)' }}>
                    Current Bet: ${gameState.currentBet.toLocaleString()}
                  </div>
                )}
              </div>

              {/* Pot chips seitlich versetzt um Ãœberlappung zu vermeiden */}
              {gameState.pot > 0 && (
                <div className="flex justify-center mt-2 -ml-16">
                  <ChipStack amount={gameState.pot} size="medium" showAmount={false} />
                </div>
              )}
            </div>

            {/* Community cards on felt */}
            <div className="flex justify-center gap-2">
              {gameState.communityCards.map((card, index) => (
                <Card key={card.id} card={card} size="medium" className="shadow-lg" />
              ))}

              {/* Placeholder for remaining community cards */}
              {Array.from({ 
                length: Math.max(0, 5 - gameState.communityCards.length) 
              }, (_, index) => (
                <Card 
                  key={`placeholder-${index}`} 
                  isHidden 
                  size="medium" 
                  className="opacity-30"
                />
              ))}
            </div>
          </div>
        </div>

        {/* Players around the table */}
        <div className="absolute inset-0 z-30">
          {gameState.players.map((player, index) => (
            <PlayerPosition
              key={player.id}
              player={player}
              isCurrentPlayer={gameState.currentPlayerIndex === index}
              isMyself={player.id === myPlayerId}
              showCards={gameState.phase === 'showdown' || player.id === myPlayerId}
              myCards={player.id === myPlayerId ? myCards : undefined}
              position={index}
              totalPositions={gameState.players.length}
            />
          ))}
        </div>

        {/* Game phase indicator */}
        <div className="absolute top-4 left-4 bg-black bg-opacity-80 rounded-lg p-3 text-white pointer-events-auto border border-amber-600 z-40">
          <div className="text-xs opacity-75">Phase</div>
          <div className="text-sm font-bold capitalize text-amber-400">{gameState.phase}</div>
          <div className="text-xs">
            Blinds: ${gameState.blinds.small}/${gameState.blinds.big}
          </div>
        </div>

        {/* Winners display with Continue Navigation */}
        {gameState.winners && showWinners && (
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-black bg-opacity-95 rounded-xl p-8 text-white text-center pointer-events-auto border-2 border-amber-500 shadow-2xl z-50">
            <h2 className="text-2xl font-bold mb-6 text-amber-400">ðŸ† Round Complete!</h2>

            {/* Winners List */}
            <div className="mb-6">
              {gameState.winners.map((winner, index) => (
                <div key={index} className="mb-3 p-3 bg-green-900 bg-opacity-40 rounded-lg border border-green-500 border-opacity-50">
                  <div className="font-semibold text-amber-200 text-lg">
                    {gameState.players.find(p => p.id === winner.playerId)?.name}
                  </div>
                  <div className="text-sm opacity-75 mb-1">{winner.hand}</div>
                  <div className="text-xl font-bold text-green-400">
                    +${winner.amount.toLocaleString()}
                  </div>
                </div>
              ))}
            </div>

            {/* ðŸŽ® Continue Navigation */}
            <div className="flex gap-4 justify-center">
              <button 
                onClick={() => {
                  // Hide winners and continue (next round will auto-start)
                  setShowWinners(false);
                }}
                className="px-6 py-3 bg-amber-600 hover:bg-amber-700 text-black font-bold rounded-lg transition-colors border-2 border-amber-400"
              >
                ðŸŽ† Continue Game
              </button>

              <button 
                onClick={() => {
                  // ðŸ  Properly return to lobby using socket client
                  socketClient.leaveRoom();
                  setCurrentRoom(null);
                  setGameState(null);
                  setMyCards([]);
                  setShowLobby(true);
                }}
                className="px-6 py-3 bg-gray-600 hover:bg-gray-700 text-white font-bold rounded-lg transition-colors border-2 border-gray-400"
              >
                ðŸ  Return to Lobby
              </button>
            </div>

            {/* Auto-continue countdown */}
            <div className="mt-4 text-amber-300 text-sm opacity-75">
              â±ï¸ Game continues automatically in 5 seconds...
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

