import React, { useState, useEffect } from 'react';
import { io, Socket } from 'socket.io-client';
import { TournamentState, TournamentBlindLevel } from '../../../../shared/types';

interface TournamentLobbyProps {
  playerName: string;
  onJoinTable: (roomId: string) => void;
  onBackToLobby?: () => void;
}

export const TournamentLobby: React.FC<TournamentLobbyProps> = ({ playerName, onJoinTable, onBackToLobby }) => {
  const [socket, setSocket] = useState<Socket | null>(null);
  const [tournaments, setTournaments] = useState<TournamentState[]>([]);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newTournament, setNewTournament] = useState({
    name: '',
    buyIn: 100,
    maxPlayers: 6,
  });

  useEffect(() => {
    const newSocket = io();
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('Connected to tournament socket');
      newSocket.emit('get-tournaments');
    });

    newSocket.on('tournaments-list', (tournamentList: TournamentState[]) => {
      setTournaments(tournamentList);
    });

    newSocket.on('tournament-created', (tournament: TournamentState) => {
      console.log('Tournament created:', tournament);
      setTournaments(prev => [...prev, tournament]);
      setShowCreateForm(false);
    });

    newSocket.on('tournament-updated', (tournament: TournamentState) => {
      setTournaments(prev => 
        prev.map(t => t.id === tournament.id ? tournament : t)
      );
    });

    newSocket.on('tournament-joined', (tournament: TournamentState) => {
      console.log('Joined tournament:', tournament);
    });

    newSocket.on('tournament-started', (tournament: TournamentState) => {
      console.log('Tournament started:', tournament);
    });

    newSocket.on('tournament-table-ready', (data: { roomId: string; gameState: any }) => {
      console.log('Tournament table ready:', data);
      onJoinTable(data.roomId);
    });

    newSocket.on('error', (error: { message: string }) => {
      alert(`Error: ${error.message}`);
    });

    return () => {
      newSocket.disconnect();
    };
  }, [onJoinTable]);

  const createTournament = () => {
    if (!socket || !newTournament.name.trim()) return;

    const blindStructure: TournamentBlindLevel[] = [
      { level: 1, smallBlind: 10, bigBlind: 20, duration: 15 },
      { level: 2, smallBlind: 15, bigBlind: 30, duration: 15 },
      { level: 3, smallBlind: 25, bigBlind: 50, duration: 15 },
      { level: 4, smallBlind: 50, bigBlind: 100, duration: 15 },
      { level: 5, smallBlind: 75, bigBlind: 150, duration: 15 },
    ];

    socket.emit('create-tournament', {
      ...newTournament,
      blindStructure,
      playerName,
    });
  };

  const joinTournament = (tournamentId: string) => {
    if (!socket) return;
    socket.emit('join-tournament', { tournamentId, playerName });
  };

  const startTournament = (tournamentId: string) => {
    if (!socket) return;
    socket.emit('start-tournament', { tournamentId });
  };

  const formatTime = (minutes: number): string => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? `${hours}h ${mins}m` : `${mins}m`;
  };

  const getTournamentStatus = (tournament: TournamentState): string => {
    switch (tournament.status) {
      case 'waiting':
        return `Wartend (${tournament.currentPlayers}/${tournament.maxPlayers})`;
      case 'active':
        const currentLevel = tournament.blindStructure[tournament.currentBlindLevel];
        return `LÃ¤uft - Level ${tournament.currentBlindLevel + 1} (${currentLevel?.smallBlind}/${currentLevel?.bigBlind})`;
      case 'completed':
        return 'Beendet';
      default:
        return 'Unbekannt';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-900 via-green-800 to-emerald-900 p-8">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <div className="text-center flex-1">
            <h1 className="text-4xl font-bold text-white mb-2">Tournament Lobby</h1>
            <p className="text-green-200">Tritt Bojek Poker Turnieren bei oder erstelle dein eigenes</p>
          </div>
          {onBackToLobby && (
            <button
              onClick={onBackToLobby}
              className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded-lg transition-colors"
            >
              â† ZurÃ¼ck zur Lobby
            </button>
          )}
        </div>

        {/* Create Tournament Button */}
        <div className="mb-8 text-center">
          <button
            onClick={() => setShowCreateForm(!showCreateForm)}
            className="bg-yellow-600 hover:bg-yellow-700 text-white font-bold py-3 px-8 rounded-lg transition-colors"
          >
            {showCreateForm ? 'Abbrechen' : 'Neues Turnier erstellen'}
          </button>
        </div>

        {/* Create Tournament Form */}
        {showCreateForm && (
          <div className="bg-black/30 backdrop-blur-sm rounded-lg p-6 mb-8 max-w-md mx-auto">
            <h3 className="text-xl font-bold text-white mb-4">Neues Turnier</h3>
            <div className="space-y-4">
              <div>
                <label className="block text-green-200 mb-2">Turniername</label>
                <input
                  type="text"
                  value={newTournament.name}
                  onChange={(e) => setNewTournament(prev => ({ ...prev, name: e.target.value }))}
                  className="w-full p-2 rounded bg-gray-800 text-white border border-gray-600"
                  placeholder="Mein Turnier"
                />
              </div>
              <div>
                <label className="block text-green-200 mb-2">Buy-In ($)</label>
                <input
                  type="number"
                  value={newTournament.buyIn}
                  onChange={(e) => setNewTournament(prev => ({ ...prev, buyIn: parseInt(e.target.value) }))}
                  className="w-full p-2 rounded bg-gray-800 text-white border border-gray-600"
                  min="1"
                />
              </div>
              <div>
                <label className="block text-green-200 mb-2">Max. Spieler</label>
                <select
                  value={newTournament.maxPlayers}
                  onChange={(e) => setNewTournament(prev => ({ ...prev, maxPlayers: parseInt(e.target.value) }))}
                  className="w-full p-2 rounded bg-gray-800 text-white border border-gray-600"
                >
                  <option value={3}>3 Spieler</option>
                  <option value={6}>6 Spieler</option>
                  <option value={9}>9 Spieler</option>
                </select>
              </div>
              <button
                onClick={createTournament}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded transition-colors"
              >
                Turnier erstellen
              </button>
            </div>
          </div>
        )}

        {/* Tournaments List */}
        <div className="grid gap-6">
          {tournaments.length === 0 ? (
            <div className="text-center text-white/70 py-12">
              <p className="text-xl">Keine aktiven Turniere</p>
              <p>Erstelle das erste Turnier!</p>
            </div>
          ) : (
            tournaments.map((tournament) => (
              <div key={tournament.id} className="bg-black/30 backdrop-blur-sm rounded-lg p-6">
                <div className="flex justify-between items-start mb-4">
                  <div>
                    <h3 className="text-2xl font-bold text-white">{tournament.name}</h3>
                    <p className="text-green-200">{getTournamentStatus(tournament)}</p>
                  </div>
                  <div className="text-right">
                    <p className="text-yellow-400 font-bold text-xl">${tournament.buyIn} Buy-In</p>
                    <p className="text-green-300">Preispool: ${tournament.prizePool}</p>
                  </div>
                </div>

                {/* Tournament Info */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4 text-sm">
                  <div className="bg-white/10 rounded p-3">
                    <p className="text-green-200">Spieler</p>
                    <p className="text-white font-bold">{tournament.currentPlayers}/{tournament.maxPlayers}</p>
                  </div>
                  <div className="bg-white/10 rounded p-3">
                    <p className="text-green-200">Status</p>
                    <p className="text-white font-bold">{tournament.status}</p>
                  </div>
                  {tournament.status === 'active' && (
                    <>
                      <div className="bg-white/10 rounded p-3">
                        <p className="text-green-200">Aktuelles Level</p>
                        <p className="text-white font-bold">{tournament.currentBlindLevel + 1}</p>
                      </div>
                      <div className="bg-white/10 rounded p-3">
                        <p className="text-green-200">Blinds</p>
                        <p className="text-white font-bold">
                          {tournament.blindStructure[tournament.currentBlindLevel]?.smallBlind}/
                          {tournament.blindStructure[tournament.currentBlindLevel]?.bigBlind}
                        </p>
                      </div>
                    </>
                  )}
                </div>

                {/* Participants */}
                <div className="mb-4">
                  <h4 className="text-white font-bold mb-2">Teilnehmer:</h4>
                  <div className="flex flex-wrap gap-2">
                    {tournament.participants.map((participant, index) => (
                      <span
                        key={index}
                        className={`px-3 py-1 rounded-full text-sm ${
                          participant.isEliminated
                            ? 'bg-red-600/30 text-red-200'
                            : 'bg-green-600/30 text-green-200'
                        }`}
                      >
                        {participant.username}
                        {participant.isEliminated && ` (${participant.position}.)`}
                        {!participant.isEliminated && ` (${participant.chipCount} Chips)`}
                      </span>
                    ))}
                  </div>
                </div>

                {/* Actions */}
                <div className="flex gap-3">
                  {tournament.status === 'waiting' && (
                    <>
                      {tournament.currentPlayers < tournament.maxPlayers && (
                        <button
                          onClick={() => joinTournament(tournament.id)}
                          className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-6 rounded transition-colors"
                        >
                          Beitreten
                        </button>
                      )}
                      {tournament.currentPlayers >= 2 && (
                        <button
                          onClick={() => startTournament(tournament.id)}
                          className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-6 rounded transition-colors"
                        >
                          Turnier starten
                        </button>
                      )}
                    </>
                  )}
                  {tournament.status === 'active' && (
                    <button
                      onClick={() => joinTournament(tournament.id)}
                      className="bg-yellow-600 hover:bg-yellow-700 text-white font-bold py-2 px-6 rounded transition-colors"
                    >
                      Zuschauen
                    </button>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

