import { create } from 'zustand';
import { subscribeWithSelector } from 'zustand/middleware';
import { Room, GameState, Player, Card } from '@shared/types';

interface PokerState {
  // Connection state
  isConnected: boolean;

  // Lobby state
  rooms: Room[];
  currentRoom: Room | null;
  currentRoomId: string | null;

  // Game state
  gameState: GameState | null;
  players: Player[];
  myCards: Card[];
  myPlayerId: string | null;

  // UI state
  selectedAction: 'fold' | 'call' | 'raise' | 'check' | 'bet' | 'all-in' | null;
  raiseAmount: number;
  showLobby: boolean;

  // Actions
  setConnected: (connected: boolean) => void;
  setRooms: (rooms: Room[]) => void;
  setCurrentRoom: (room: Room | null) => void;
  setCurrentRoomId: (roomId: string | null) => void;
  setGameState: (gameState: GameState | null) => void;
  setPlayers: (players: Player[]) => void;
  setMyCards: (cards: Card[]) => void;
  setMyPlayerId: (playerId: string | null) => void;
  setSelectedAction: (action: 'fold' | 'call' | 'raise' | 'check' | 'bet' | 'all-in' | null) => void;
  setRaiseAmount: (amount: number) => void;
  setShowLobby: (show: boolean) => void;

  // Computed getters
  getCurrentPlayer: () => Player | null;
  getMyPlayer: () => Player | null;
  isMyTurn: () => boolean;
  canCheck: () => boolean;
  canCall: () => boolean;
  canRaise: () => boolean;
  getCallAmount: () => number;
  getMinRaise: () => number;
  getMaxRaise: () => number;
}

export const usePoker = create<PokerState>()(
  subscribeWithSelector((set, get) => ({
    // Initial state
    isConnected: false,
    rooms: [],
    currentRoom: null,
    currentRoomId: null,
    gameState: null,
    players: [],
    myCards: [],
    myPlayerId: null,
    selectedAction: null,
    raiseAmount: 0,
    showLobby: true,

    // Actions
    setConnected: (connected) => set({ isConnected: connected }),
    setRooms: (rooms) => set({ rooms }),
    setCurrentRoom: (room) => set({ currentRoom: room }),
    setCurrentRoomId: (roomId) => set({ currentRoomId: roomId }),
    setGameState: (gameState) => set({ gameState }),
    setPlayers: (players) => set({ players }),
    setMyCards: (cards) => set({ myCards: cards }),
    setMyPlayerId: (playerId) => set({ myPlayerId: playerId }),
    setSelectedAction: (action) => set({ selectedAction: action }),
    setRaiseAmount: (amount) => set({ raiseAmount: amount }),
    setShowLobby: (show) => set({ showLobby: show }),

    // Computed getters
    getCurrentPlayer: () => {
      const { gameState } = get();
      if (!gameState || !gameState.isStarted) return null;
      return gameState.players[gameState.currentPlayerIndex] || null;
    },

    getMyPlayer: () => {
      const { gameState, myPlayerId } = get();
      if (!gameState || !myPlayerId) return null;
      return gameState.players.find(p => p.id === myPlayerId) || null;
    },

    isMyTurn: () => {
      const { getCurrentPlayer, myPlayerId } = get();
      const currentPlayer = getCurrentPlayer();
      return currentPlayer?.id === myPlayerId;
    },

    canCheck: () => {
      const { getMyPlayer, gameState } = get();
      const myPlayer = getMyPlayer();
      if (!myPlayer || !gameState) return false;
      return myPlayer.currentBet === gameState.currentBet;
    },

    canCall: () => {
      const { getMyPlayer, gameState } = get();
      const myPlayer = getMyPlayer();
      if (!myPlayer || !gameState) return false;
      return myPlayer.currentBet < gameState.currentBet && myPlayer.chips > 0;
    },

    canRaise: () => {
      const { getMyPlayer } = get();
      const myPlayer = getMyPlayer();
      if (!myPlayer) return false;
      return myPlayer.chips > 0;
    },

    getCallAmount: () => {
      const { getMyPlayer, gameState } = get();
      const myPlayer = getMyPlayer();
      if (!myPlayer || !gameState) return 0;
      return Math.min(
        gameState.currentBet - myPlayer.currentBet,
        myPlayer.chips
      );
    },

    getMinRaise: () => {
      const { gameState } = get();
      if (!gameState) return 0;
      return gameState.blinds.big;
    },

    getMaxRaise: () => {
      const { getMyPlayer } = get();
      const myPlayer = getMyPlayer();
      if (!myPlayer) return 0;
      return myPlayer.chips;
    }
  }))
);

server/poker/botPlayer.ts.content:
import { Player, GameState, PokerAction, Card } from '../../shared/types.js';

export interface BotPersonality {
  name: string;
  aggression: number; // 0-1, how likely to bet/raise vs call
  tightness: number; // 0-1, how selective with starting hands  
  bluffFrequency: number; // 0-1, how often to bluff
}

export const BOT_PERSONALITIES: BotPersonality[] = [
  {
    name: "Conservative Carl",
    aggression: 0.2,
    tightness: 0.8,
    bluffFrequency: 0.1
  },
  {
    name: "Steady Sam", 
    aggression: 0.4,
    tightness: 0.6,
    bluffFrequency: 0.2
  },
  {
    name: "Aggressive Annie",
    aggression: 0.7,
    tightness: 0.3,
    bluffFrequency: 0.4
  },
  {
    name: "Loose Larry",
    aggression: 0.5,
    tightness: 0.2,
    bluffFrequency: 0.3
  }
];

export class BotPlayer {
  public id: string;
  public name: string;
  public personality: BotPersonality;
  public isBot: boolean = true;

  constructor(botId: string, personality?: BotPersonality) {
    this.id = `bot_${botId}`;
    this.personality = personality || BOT_PERSONALITIES[Math.floor(Math.random() * BOT_PERSONALITIES.length)];
    this.name = this.personality.name;
  }

  /**
   * Create a Player object for this bot
   */
  public createPlayerObject(chips: number, position: number): Player {
    return {
      id: this.id,
      userId: undefined, // Bots don't have user IDs
      name: this.name,
      chips,
      cards: [],
      currentBet: 0,
      totalBet: 0,
      position,
      isActive: true,
      isFolded: false,
      isAllIn: false
    };
  }

  /**
   * Simple hand strength evaluation (0-1)
   * This is a very basic implementation for MVP
   */
  private evaluateHandStrength(holeCards: Card[], communityCards: Card[]): number {
    if (holeCards.length !== 2) return 0;

    const card1Value = this.getCardValue(holeCards[0].rank);
    const card2Value = this.getCardValue(holeCards[1].rank);
    const isPair = card1Value === card2Value;
    const isSuited = holeCards[0].suit === holeCards[1].suit;
    const highCard = Math.max(card1Value, card2Value);
    const lowCard = Math.min(card1Value, card2Value);

    let strength = 0;

    // Pre-flop hand strength
    if (isPair) {
      strength = 0.5 + (highCard / 14) * 0.4; // Pairs are strong
    } else if (isSuited) {
      strength = 0.2 + (highCard + lowCard) / 28 * 0.3; // Suited cards bonus
    } else {
      strength = (highCard + lowCard) / 28 * 0.4; // High cards
    }

    // Adjust for community cards (very simple)
    if (communityCards.length > 0) {
      // Check for potential pairs, flushes, straights (simplified)
      const allCards = [...holeCards, ...communityCards];
      const ranks = allCards.map(c => this.getCardValue(c.rank));
      const suits = allCards.map(c => c.suit);

      // Look for pairs
      const rankCounts: { [key: number]: number } = {};
      ranks.forEach(rank => {
        rankCounts[rank] = (rankCounts[rank] || 0) + 1;
      });

      const maxCount = Math.max(...Object.values(rankCounts));
      if (maxCount >= 2) strength += 0.2; // Has a pair or better
      if (maxCount >= 3) strength += 0.3; // Has three of a kind or better

      // Look for flush draws
      const suitCounts: { [key: string]: number } = {};
      suits.forEach(suit => {
        suitCounts[suit] = (suitCounts[suit] || 0) + 1;
      });
      const maxSuitCount = Math.max(...Object.values(suitCounts));
      if (maxSuitCount >= 4) strength += 0.1; // Flush draw
    }

    return Math.min(strength, 1);
  }

  private getCardValue(rank: string): number {
    const values: { [key: string]: number } = {
      'A': 14, 'K': 13, 'Q': 12, 'J': 11, '10': 10,
      '9': 9, '8': 8, '7': 7, '6': 6, '5': 5, '4': 4, '3': 3, '2': 2
    };
    return values[rank] || 0;
  }

  /**
   * Make a decision based on current game state
   */
  public makeDecision(gameState: GameState, playerId: string): PokerAction {
    const player = gameState.players.find(p => p.id === playerId);
    if (!player) {
      return { type: 'fold', playerId };
    }

    const handStrength = this.evaluateHandStrength(player.cards, gameState.communityCards);
    const potOdds = gameState.pot > 0 ? (gameState.currentBet - player.currentBet) / (gameState.pot + gameState.currentBet - player.currentBet) : 0;

    // Adjust hand strength based on personality
    let adjustedStrength = handStrength;
    adjustedStrength *= (1 + this.personality.aggression * 0.2); // Aggressive bots value hands higher
    adjustedStrength *= (1 + (1 - this.personality.tightness) * 0.1); // Loose bots value hands higher

    const needToCall = gameState.currentBet - player.currentBet;
    const canCheck = needToCall === 0;

    // Very simple decision tree for MVP
    if (adjustedStrength < 0.2) {
      // Weak hand - usually fold
      if (canCheck) {
        return { type: 'check', playerId };
      } else {
        return { type: 'fold', playerId };
      }
    } else if (adjustedStrength < 0.5) {
      // Mediocre hand - call or check
      if (canCheck) {
        return { type: 'check', playerId };
      } else if (needToCall <= player.chips * 0.1) { // Only call small bets
        return { type: 'call', playerId };
      } else {
        return { type: 'fold', playerId };
      }
    } else if (adjustedStrength < 0.8) {
      // Good hand - call or small bet
      if (canCheck && Math.random() < this.personality.aggression) {
        // Sometimes bet with good hands
        const betAmount = Math.floor(gameState.pot * 0.5 + Math.random() * gameState.pot * 0.3);
        return { type: 'bet', playerId, amount: Math.min(betAmount, player.chips) };
      } else if (canCheck) {
        return { type: 'check', playerId };
      } else {
        return { type: 'call', playerId };
      }
    } else {
      // Strong hand - bet or raise
      if (canCheck) {
        const betAmount = Math.floor(gameState.pot * 0.7 + Math.random() * gameState.pot * 0.5);
        return { type: 'bet', playerId, amount: Math.min(betAmount, player.chips) };
      } else if (Math.random() < this.personality.aggression) {
        const raiseAmount = needToCall + Math.floor(gameState.pot * 0.5);
        return { type: 'raise', playerId, amount: Math.min(raiseAmount, player.chips) };
      } else {
        return { type: 'call', playerId };
      }
    }
  }

  /**
   * Create multiple bot players for a game
   */
  public static createBots(count: number, startingChips: number): BotPlayer[] {
    const bots: BotPlayer[] = [];
    const usedPersonalities = new Set<number>();

    for (let i = 0; i < count; i++) {
      let personalityIndex;
      do {
        personalityIndex = Math.floor(Math.random() * BOT_PERSONALITIES.length);
      } while (usedPersonalities.has(personalityIndex) && usedPersonalities.size < BOT_PERSONALITIES.length);

      usedPersonalities.add(personalityIndex);
      const personality = BOT_PERSONALITIES[personalityIndex];
      bots.push(new BotPlayer(`${i + 1}`, personality));
    }

    return bots;
  }
}

