  return translations[language][key];
}

// Template string helper
export function translateTemplate(language: Language, key: keyof Translations, values: Record<string, string> = {}): string {
  let text = getTranslation(language, key);

  Object.entries(values).forEach(([placeholder, value]) => {
    text = text.replace(`{${placeholder}}`, value);
  });

  return text;
}

client/src/lib/stores/useLanguage.tsx.content:
import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import { Language, getTranslation, translateTemplate, Translations } from '../i18n';

interface LanguageState {
  currentLanguage: Language;
  setLanguage: (language: Language) => void;
  t: (key: keyof Translations, values?: Record<string, string>) => string;
}

export const useLanguage = create<LanguageState>()(
  persist(
    (set, get) => ({
      currentLanguage: 'de', // Default to German for Bogec Poker

      setLanguage: (language: Language) => {
        set({ currentLanguage: language });
      },

      t: (key: keyof Translations, values?: Record<string, string>) => {
        const { currentLanguage } = get();
        if (values) {
          return translateTemplate(currentLanguage, key, values);
        }
        return getTranslation(currentLanguage, key);
      },
    }),
    {
      name: 'bojek-poker-language',
      partialize: (state) => ({ currentLanguage: state.currentLanguage }),
    }
  )
);

