  }

  public static spinRoulette(): number {
    return this.ROULETTE_NUMBERS[Math.floor(Math.random() * this.ROULETTE_NUMBERS.length)];
  }

  public static getRandomEvent(): IronicEvent {
    // Weighted random selection
    const totalWeight = this.EVENTS.reduce((sum, event) => sum + event.probability, 0);
    let random = Math.random() * totalWeight;

    for (const event of this.EVENTS) {
      random -= event.probability;
      if (random <= 0) {
        return event;
      }
    }

    // Fallback to first event
    return this.EVENTS[0];
  }

  public static executeEvent(event: IronicEvent, gameState: GameState): IronicEventResult {
    console.log(`[IRONIC EVENT] Executing: ${event.name}`);
    return event.effect(gameState);
  }

  public static getRouletteBasedEvent(rouletteNumber: number): IronicEvent | null {
    // Map roulette numbers to specific events for extra fun
    if (rouletteNumber === 0) return this.EVENTS.find(e => e.id === 'lucky_jackpot') || null;
    if (rouletteNumber >= 1 && rouletteNumber <= 12) return this.EVENTS.find(e => e.id === 'money_rain') || null;
    if (rouletteNumber >= 13 && rouletteNumber <= 24) return this.EVENTS.find(e => e.id === 'robin_hood') || null;
    if (rouletteNumber >= 25 && rouletteNumber <= 36) return this.EVENTS.find(e => e.id === 'market_crash') || null;

    return null; // Sometimes no event happens
  }

  public static getAllEvents(): IronicEvent[] {
    return [...this.EVENTS];
  }
}

