  description: string;
  descriptionDE: string;
  timestamp: number;
  affectedPlayers?: string[];
  specialEffect?: string;
}

interface IronicEventModalProps {
  event: IronicEventNotification | null;
  rouletteNumber?: number;
  onClose: () => void;
}

const specialEffectEmojis: Record<string, string> = {
  money_rain: 'ðŸ’°',
  robin_hood: 'ðŸ¹',
  reverse_world: 'ðŸ”„',
