  crash: 'ðŸ“‰',
  jackpot: 'ðŸŽ°',
  card_chaos: 'ðŸƒ',
  losers_luck: 'ðŸ€',
};

const getRouletteColor = (number: number): string => {
  if (number === 0) return 'text-green-400';
  if ([1, 3, 5, 7, 9, 12, 14, 16, 18, 19, 21, 23, 25, 27, 30, 32, 34, 36].includes(number)) {
    return 'text-red-400';
  }
  return 'text-gray-900';
};

const getSpecialEffectAnimation = (effect: string) => {
  switch (effect) {
    case 'money_rain':
      return {
        animate: {
          scale: [1, 1.2, 1],
          rotate: [0, 10, -10, 0],
          y: [-20, 0, -5, 0],
        },
        transition: { duration: 2, repeat: 1 }
      };
    case 'jackpot':
      return {
        animate: {
          scale: [1, 1.5, 1],
          rotate: [0, 360],
        },
        transition: { duration: 1.5, repeat: 2 }
      };
    case 'reverse_world':
      return {
        animate: {
          rotateY: [0, 180, 360],
          scale: [1, 1.1, 1],
        },
        transition: { duration: 2, repeat: 1 }
      };
    case 'crash':
      return {
        animate: {
          x: [-10, 10, -5, 5, 0],
          y: [0, -5, 5, -2, 0],
        },
        transition: { duration: 0.8, repeat: 2 }
      };
    default:
      return {
        animate: {
          scale: [1, 1.1, 1],
        },
        transition: { duration: 1, repeat: 1 }
      };
  }
};

